      var table_column_list = [];
      var table_data_list = [];
      var table_data_dict = {}

      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]
            keys_list.push(key)
            if (key != "label"){
            ct_list1.push(key)

            }
            else{}

            var value = data[key]
            if (this.state.value_bar != value){bar_value_list.push(value)}
            else {
            }

