        keys_list.push(key);

        if (key != "label") {
          ct_list1.push(key);
        } else {}

        var value = data[key];

        if (this.state.value_bar != value) {
          bar_value_list.push(value);
        } else {}

        var data_dict = {};
        var load_data_dict = {};

        if (key != "label") {
          data_dict['name'] = key;
          load_data_dict['item'] = key;
          data_dict['value'] = value;
          load_data_dict['count'] = value;
          load_chart_list.push(data_dict);
          load_donut_chart_list.push(load_data_dict);
          var column_dict = {};
          var table_data_dict = {};
          column_dict['title'] = key;
          column_dict['dataIndex'] = key;
          column_dict['key'] = key;
          table_data_dict[key] = value;
          table_column_list.push(column_dict);
          table_data_list.push(table_data_dict);
        } else {}
      }

      this.setState({
        bar_data: load_chart_list
      });
      this.setState({
        ve_bar_data: load_chart_list
      });
      this.setState({
        sourceData: load_donut_chart_list
      });
      this.setState({
        sourceData_donut: load_donut_chart_list
      });
    }
  }

