    key: 'total',
  },
  {
    title: 'Conf',
    dataIndex: 'total',
    key: 'total',
  }




  ]

  const table_data = [
  {
    key: '1',
    oNumber: 'DVO524',
    confDate: '05/07/2019',
    customer: 'Jone Deo',
    salePer: 'SQBL',
    total: '$26300',
    tags: ['Success'],
  },
  {
    key: '1',
    oNumber: 'DVO524',
    confDate: '05/07/2019',
    customer: 'Jone Deo',
    salePer: 'SQBL',
    total: '$26300',
    tags: ['Success'],
  }


  ]




      return (
        <Modal visible={visible}
          title="Create a new collection"
          okText="Download"
          onCancel={onCancel}
          onOk={onCreate}
          >
         <Table columns={table_columns} dataSource={table_data} style={{overflowY:"scroll"}}/>
        </Modal>
      );
    }
  }
);


const DataSet = require('@antv/data-set');
const scale = [{
  dataKey: 'percent',
  min: 0,
  formatter: '.0%',
}];


const scale1 = [{
  dataKey: 'percent',
  min: 0,
  formatter: '.0%',
}];



const sourceData = [
  { name: 'A', value: 131744 },
  { name: 'B', value: 104970 },
  { name: 'C', value: 29034 },
  { name: 'D', value: 23489 },
  { name: 'E', value: 18203 },
];




const vertical_data = [
  { name: 'A', value: 30000000 },
  { name: 'B', value: 40000000 },
  { name: 'C', value: 530000000 },

];

const vertical_scale = [{
  dataKey: 'value',
  min: 0,
  formatter: function formatter(val) {
             if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }

                         else {
                                    return val;
                                }
        }
},{
  dataKey: 'year',
  min: 0,
  max: 1,
}];





























var categories_data = [];

var months = categories_data.map((item) => item.month).filter((item, index, array) => array.indexOf(item) == index)


const productTotals = categories_data.reduce((obj, curr) => {
    if(!obj[curr.name]){
        obj[curr.name] = []
    }

    obj[curr.name][months.indexOf(curr.month)] = parseInt(curr.records)
    return obj
}, {})

const series = Object.entries(productTotals).map(([name, prodArr]) => {
    return {
        name: name,
        data: months.map((month, monthIndex) => {
            if(!prodArr[monthIndex]){
                return 0
            } else {
                return prodArr[monthIndex]
            }

        })
    }

})



