      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]
            keys_list.push(key)
            var value = data[key]
            line_value_list.push(value)
            var data_dict = {};
            data_dict['name']=key;
