import { Chart, Tooltip, Axis, Legend, Pie, Coord, Line, Bar, Point} from 'viser-react';
import * as React from 'react';
import { Row, Col, Card, Button, Icon, Progress } from "antd";
import axios from "axios";
import ReactApexChart from 'react-apexcharts'

class AnalyticalTest extends React.Component {

      constructor(props) {
        super(props);

        this.state = {
          options: {
            dataLabels: {
              enabled: false
            },

            stroke: {
              width: [1, 1, 4]
            },
            title: {
              text: 'XYZ - Stock Analysis (2009 - 2016)',
              align: 'left',
              offsetX: 110
            },
            xaxis: {
              categories: [],

              labels: {
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                }
            },

            tooltip: {
              fixed: {
                enabled: true,
                position: 'topLeft', // topRight, topLeft, bottomRight, bottomLeft
                offsetY: 30,
                offsetX: 60
              },
            },
            legend: {
              horizontalAlign: 'left',
              offsetX: 40
            }
          },

          series: [{name: 'Income',type: 'column', data: [1000000,2000000,4000000]},
          { name: 'Cashflow', type: 'column', data: [10000000,20000000,40000000]}

          ],
        }
      }

      render() {
        return (


          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="line" width="520" height="350" />
          </div>


        );
      }
    }

export default AnalyticalTest;
