                opposite: true,
                axisTicks: {
                  show: true,
                },
                axisBorder: {
                  show: true,
                  color: '#FEB019'
                },
                 labels: {
                   style: {
                    color: '#FEB019',
                  },
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                },
                title: {
                  text: "Revenue (thousand crores)",
                  style: {
                    color: '#FEB019',
                  }
                }
              },
            ],
            tooltip: {
              fixed: {
                enabled: true,
                position: 'topLeft', // topRight, topLeft, bottomRight, bottomLeft
                offsetY: 30,
                offsetX: 60
              },
            },
            legend: {
              horizontalAlign: 'left',
              offsetX: 40
            }
          },
          series: [{name: 'Income',type: 'column', data: [1000000]},
          { name: 'Cashflow', type: 'column', data: [50000000]},
          {name: 'Income1',type: 'column', data: [1000000]},
          { name: 'Cashflow1', type: 'column', data: [50000000]},
          { name: 'Revenue', type: 'line',data: [30,29],
          }, ],
        }
      }

      render() {
        return (


          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="line" width="520" height="350" />
          </div>


        );
