import React, { Component } from "react";
import { Row, Col, Card, Button, Icon, Progress,Table } from "antd";
import ReactApexChart from 'react-apexcharts';

import ChartBreakdownDonut from "./ChartBreakdownDonut";
import LineChart from "./LineChart";
import BarChart from "./BarChart";


const DataSet = require("@antv/data-set");




    class ManagementAllCharts extends React.Component {

      constructor(props) {
        super(props);

        this.state = {
         cat:[['Feb'],['test']],

        area_chart_series:[{
            name: 'series1',
            data: [31456456455.196860945876, 19456456.196860945876]
          },
          {
            name: 'series1',
            data: [31645645645, 196860945876]
          }


          ],
        }
      }



      render() {

      const options = {
            chart: {
              stacked: true,
              events: {
                selection: function (chart, e) {
                  console.log(new Date(e.xaxis.min))
                }
              },
            },
            colors: ['#008FFB', '#00E396', '#CED4DC'],
            dataLabels: {
              enabled: false
            },
            stroke: {
              curve: 'smooth'
            },
            fill: {
              type: 'gradient',
              gradient: {
                opacityFrom: 0.6,
                opacityTo: 0.8,
              }
            },
            legend: {
              position: 'top',
              horizontalAlign: 'left'
            },
            xaxis: {
              type: 'datetime'
            }
          }


        return (


          <div id="chart">
            <ReactApexChart options={options} series={this.state.area_chart_series} type="area"  width="600" height="350" />
          </div>


        );
      }
    }
export default ManagementAllCharts;
