          },

          ],

           series1: [{
            name: 'SQBL',
            data: [44]
          }, {
            name: 'SQCL',
            data: [53]
          }],

        }
      }


  render() {
    return (
    <Col xs={24} sm={24} md={12} lg={24} className="c-mb">

                <Card
                  bordered={false}
                  className="mi-card mi-card-boxshadow card-statistics"
                  style={{ minHeight: "100%" }}
                >
                  <div className="card-statistics-block">

                  <ReactApexChart options={this.state.options1} series={this.state.series1} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options2} series={this.state.series2} type="" width={300} height={150} />
                     </div>

              <div className="card-statistics-block">

                  <ReactApexChart options={this.state.options1} series={this.state.series1} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options2} series={this.state.series2} type="" width={300} height={150} />
                     </div>

                </Card>



            </Col>




    )
  }
}

