		dataPoints: [
			{ y: 236, label: "Italy" },
			{ y: 172, label: "China" },
			{ y: 309, label: "France" },
			{ y: 302, label: "Great Britain" },
			{ y: 285, label: "Germany" },
			{ y: 188, label: "Russia" },
			{ y: 788, label: "USA" }
		]
	}]



		}

		const options1 = {
			title: {
				text: "NET PROFIT / LOSS"
			},


			animationEnabled: true,
			data: [{
		type: "column",
		name: "Proven Oil Reserves (bn)",
		legendText: "Actual",
		showInLegend: true,
		color: "blue",
		dataPoints:[
			{ label: "SQBL", y: 266.21 },
			{ label: "SQCL", y: 302.25 },

		]
	},
	{
		type: "column",
		name: "Oil Production (million/day)",
		text:"Status",
		legendText: "Budget",
		axisYType: "secondary",
		showInLegend: true,
		color: "#1df20a",
		dataPoints:[
			{ label: "SQBL", y: 10.46 },
			{ label: "SQCL", y: 2.27 },

		]
	}]


		}
const options2 = {
			animationEnabled: true,
			title: {
				text: "BUDGET ACHIEVEMENT"
			},
			subtitles: [{
				text: "100.00% Positive",
				verticalAlign: "center",
				fontSize: 50,

			}],
			data: [{
				type: "doughnut",
				color: "#1df20a",
				showInLegend: true,


			}]
		}
		return (
		<div>
			<h1>FINANCIAL YEAR</h1>

<ul className="card-statistics-block">
			<button  className="btn btn-outline-secondary "><span>2019</span></button>
			</ul>
			<ul className="card-statistics-block">


	  <a class="btn btn-outline-secondary" href="#">Jul</a>

      <a class="btn btn-outline-secondary" href="#">Aug</a>
