    e.preventDefault();
     this.loadLineChartData(
         {A:this.state.value_xl, B:this.state.value_yl}
  );
  };


async loadLineChartData(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }
