import axios from "axios";


const t = [10, 20, 32, 34,5,63,6,75,7]



class AnalyticalTest extends React.Component {
  constructor(props) {
    super(props);
   this.state = {



        options1: {
            plotOptions: {
              bar: {
                horizontal: false,
                columnWidth: '55%',
                endingShape: 'rounded'
              },
            },
            dataLabels: {
              enabled: false
            },
            stroke: {
              show: true,
              width: 2,
              colors: ['transparent']
            },
            xaxis: {
              categories: ['Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct'],
            },
            yaxis: {
              title: {
                text: '$ (thousands)'
              }
            },
            fill: {
              opacity: 1
            },
            tooltip: {
              y: {
                formatter: function (val) {
                  return "$ " + val + " thousands"
                }
              }
            }
          },
          series1: [{
            name: 'Net Profit',
            data: [44, 55, 57, 56, 61, 58, 63, 60, 66]
          }, {
            name: 'Revenue',
            data: [76, 85, 101, 98, 87, 105, 91, 114, 94]
          }],






   options2: {
            plotOptions: {
              bar: {
                horizontal: true,
                dataLabels: {
                  position: 'top',
                },
              }
            },






            chart:{
               width:'200%'
            },



            dataLabels: {
              enabled: true,
              offsetX: -6,
              style: {
                fontSize: '12px',
                colors: ['#fff']
              }
            },



            stroke: {
              show: true,
              width: 1,
              colors: ['#fff']
            },

            xaxis: {
              categories: ["Amount_USD"],
            }
          },

          series2:[


          ],


    books:[],
    filter:[],
     g:[],
     x:[],
     y:[]

      }
  }



  componentWillMount() {
    this.loadAnalytical();


  }

  handleSubmit = (e) => {
     this.setState({value: e.target.value});
 };


handleSubmit = (e) => {
    e.preventDefault();
    this.setState({value: e.target.value});
     this.loadCheckbox(
         {Column:e.target.value }
  );
  };

handleSubmit12 = (e) => {
    e.preventDefault();

     this.loadData(
         {A:this.state.value, B:this.state.value1}
  );
  };




handleSubmit1 = (e) => {
    e.preventDefault();
    this.setState({value1: e.target.value});
     this.loadCheckbox(
         {Column:e.target.value }
  );
  };


  handleSubmit1 = (e) => {
    e.preventDefault();
    this.setState({value1: e.target.value});
     this.loadCheckbox(
         {Column:e.target.value }
  );
  };


  async loadAnalytical()
  {
    const promise = await axios.post("http://127.0.0.1:8070/api/v1/sqd_api", { firstName: 'Mokka Ravi', lastName: 'Flintstone' });
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);

      this.setState({books:data});
      const d = [{'name': 'Actual','data': []}, {'name': 'Budget','data': []}]
      this.setState({y:d})
       console.log(this.state.y);

    }
  }

  async loadCheckbox(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }

    const promise = await axios.post("http://127.0.0.1:8070/api/v1/filter_api", options);
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      this.setState({filter:data});
      console.log(data);
    }

}



async loadData(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }
    this.setState({y : []})
    const promise = await axios.post("http://127.0.0.1:8070/api/v1/data", options);
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      this.setState(data);


      const g_data = [this.state.Actual, this.state.Budget]
      this.setState({g:g_data})
      const d = [{'name': 'Actual','data': [this.state.Actual]}, {'name': 'Budget','data': [this.state.Budget]}]
      this.setState({y:d})
      console.log(data);
    }

}







  render() {
    return (
      <div>
        <div id="content">



           <Row type="flex" gutter={24}>


           <Col xs={24} sm={24} md={{span: 12, order: 4}} lg={12} xl={{span:12, order: 5}} className="c-mb">
              <Card
                title="Grouped Bar Chart"
                style={{ minHeight: "100%" }}
                bordered={false}
                className="mi-card mi-card-header-borderless mi-card-boxshadow" >
                <div id="chart">
                <div>

<select class="custom-select"  value={this.state.value} onChange={this.handleSubmit}
                    style={{ width:"110px", marginLeft:"20px" }}>
                     <option  value="" >Xaxis</option>
                     {this.state.books.map((value,index)=>
                              {return   <option class="btn btn-custom btn-block btn-detail"
                              style={{ cursor: "pointer;", fontSize: "14px"}} key={index}> {value} </option> }
                              )}


                             </select>


<select class="custom-select"  value={this.state.value1} onChange={this.handleSubmit1}
                    style={{ width:"110px", marginLeft:"20px" }}>
                     <option  value="" >Yaxis</option>
                     {this.state.books.map((value,index)=>
                              {return   <option class="btn btn-custom btn-block btn-detail"
                              style={{ cursor: "pointer;", fontSize: "14px"}} key={index}> {value} </option> }
                              )}


                             </select>

<React.Fragment>
    <button onClick={this.handleSubmit12} className='btn btn-info' style={{marginLeft:"20px"}}
      type='button'>OK</button>
    </React.Fragment>


                    </div>

            <ReactApexChart options={this.state.options2} series={this.state.y} type="bar" width="420" height="350" />
          </div>
              </Card>
            </Col>
          </Row>
          <Row type="flex" gutter={24}>

          </Row>
        </div>
      </div>
    );
  }
}
export default AnalyticalTest;
