import React, { Component } from "react";
import { Row, Col, Card, Avatar, Tabs, Form, Icon, Input, Button, Checkbox, Typography} from "antd";
import 'bootstrap/dist/css/bootstrap.min.css';
import Select from 'react-select';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { Host } from  "../../Host";
import * as styles  from "./user.scss"
import swal from 'sweetalert';
import axios from "axios";

const { TabPane } = Tabs;
const options = [
  { value: 'a', label: 'Financial' },
  { value: 'b', label: 'Operational' },
  { value: 'c', label: 'Management' },
];

class AddUser extends Component {

   constructor(props) {
    super(props);
    this.state = {
     userName:'',
     userId:'',
     errorMsg:'',
     Account_type:'',
     selectedOptions: [],
    }
 }
handleChange = (selectedOptions) => {
    this.setState({ selectedOptions });
  }


  handleSubmitCreateUser = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
    e.preventDefault();
        const req_data = {
                'first_name' : e.target.fname.value,
                'last_name' : e.target.lname.value,
                'username' : e.target.username.value,
                'user_rights' : e.target.user_rights.value,
                'password' : e.target.password.value,
                'account_type' :'sqd',
                'company' : 'bluescheme',
                'title' :  'create',
                'office_number' : e.target.officePhone.value,
                'cell_number' : e.target.cellPhone.value,
                'home_number' : e.target.homePhone.value,
                'email' : e.target.email.value,
                'address1' : e.target.address_1.value,
                'address2' : e.target.address_2.value,
                'city' : e.target.city.value,
                'state' : e.target.state.value,
                'zip_code' : e.target.zipCode.value,
                'flog_value': 'addUser'}
    this.UserCreateAPI(req_data);

   });
};

async UserCreateAPI(req_data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(req_data),
    }
    try{
     const promise = await axios.post(Host.loginURL+"/signup_api", options);
     const status = promise.status;
     if(status==200)
    {
      const data = promise.data.data;
     }
  }
  catch(err){this.setState({msg: "Your Password Not Changed"});}
}


    render () {

    const { selectedOption } = this.state;

        const formItemLayout = {
            labelCol: {
            xs: { span: 24 },
            sm: { span: 8 },
            },
            wrapperCol: {
            xs: { span: 24 },
            sm: { span: 16 },
            },
        };
        const tailFormItemLayout = {
            wrapperCol: {
            xs: {
                span: 24,
                offset: 0,
            },
            sm: {
                span: 16,
                offset: 8,
            },
            },
        };
        const { getFieldDecorator } = this.props.form;
   return(
     <div>
        <div id="content">
          <Row gutter={24} type="flex">
            <Col span={16} className="c-mb">
              <Card
                bordered={false}
                className="mi-card mi-card-boxshadow mi-card-user-setting"
              >
                    <div className="tabpane-inside-gutters">
                    <Row type="flex" justify="">
                        <Col span={16}>

                         <div className="page-header">
                          <Row type="flex" justify="space-between">
                                <Col>
                                  <h3 lassName="page-heading" ></h3>
                                </Col>
                          </Row>
                      </div>
             <Form  {...formItemLayout}  onSubmit={this.handleSubmitCreateUser}>

                <Row>
                    <Col span={16} offset={8}>
                       <div class="ant-card-head-wrapper">
                        <h5 class="ant-card-head-title">Create User</h5>
                       </div>
                    </Col>
                </Row>
                <Form.Item label="First Name">
                    {getFieldDecorator('fname', {rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="Last Name">
                    {getFieldDecorator('lname',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="Username">
                    {getFieldDecorator('username', {rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="UserRights">
                    {getFieldDecorator('user_rights',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (
                  <select class="custom-select" id="multiple-option" onChange={this.multiSelection}>
                     <option>Operational</option>
                     <option>Financial</option>
                     <option>Management</option>
                  </select>


                    )}

                </Form.Item>
                <Form.Item label="Password">
                    {getFieldDecorator('password',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="Office Phone">
                    {getFieldDecorator('officePhone',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="Cell Phone">
                    {getFieldDecorator('cellPhone',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="Home Phone">
                    {getFieldDecorator('homePhone',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input  value='hi'/>)}
                </Form.Item>
                <Form.Item label="Email">
                    {getFieldDecorator('email',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input  value='hi'/>)}
                </Form.Item>

                <Row>
                    <Col span={16} offset={8}>
                        <h6 className="mb-3">Address</h6>
                    </Col>
                </Row>
                <Form.Item label="Address 1">
                    {getFieldDecorator('address_1',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="Address 2">
                    {getFieldDecorator('address_2',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="City">
                    {getFieldDecorator('city',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                <Form.Item label="State">
                    {getFieldDecorator('state',{rules:[{required: true,
                        message: "Please input your !"}]} )
                    (<Input />)}
                </Form.Item>
                    <Form.Item label="Zip Code">
                        {getFieldDecorator('zipCode',{rules:[{required: true,
                            message: "Please input your !"}]} )
                        (<Input />)}
                    </Form.Item>
                  <Form.Item>
                <Select
                        isMulti
                        value={selectedOption}
                        onChange={this.handleChange}
                        options={options}
                        classNamePrefix="mySelect" />


               </Form.Item>
                <Form.Item {...tailFormItemLayout}>
                    <Button type="primary" htmlType="submit">
                        Register
                    </Button>
                </Form.Item>



            </Form>
                      </Col>
                    </Row>
                </div>
              </Card>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
const WrappedAddUser = Form.create()(AddUser);
export default WrappedAddUser;
