import { Chart, Axis, Tooltip, Polygon, Line, Point} from 'viser-react';
import * as React from 'react';
import * as $ from 'jquery';
const DataSet = require('@antv/data-set');


const data = [[0, 0, 10], [0, 1, 19], [0, 2, 8], [0, 3, 24], [0, 4, 67]]

const source = [];
for (let i = 0; i < data.length; i++) {
    const item = data[i];
    const obj: any = {};
    obj.name = item[0];
    obj.day = item[1];
    obj.sales = item[2];
    source.push(obj);
}

class Reactjs extends React.Component {

    render() {
        const scale = [
            {
                dataKey: 'name',
                type: 'cat',
                values: ['Alexander', 'Marie', 'Maximilian', 'Sophia', 'Lukas', 'Maria', 'Leon', 'Anna', 'Tim', 'Laura']
            },
            {
                dataKey: 'day',
                type: 'cat',
                values: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday']
            }
        ];
        return (
         <Chart forceFit height={400} data={source} scale={scale}>



                <Axis
                    dataKey="day"
                    title={null}
                    grid={
                        {
                            align: 'center',
                            lineStyle: {
                                lineWidth: 1,
                                lineDash: [2, 2],
                                stroke: '#d9d9d9'
                            },
                            // showFirstLine: true
                        }
                    }
                />
                <Tooltip />
                 <Line position="year*value" />
                  <Point position="year*value" shape="circle"/>

                <Polygon
                    position="name*day"
                    color={['sales', '#f6f6f6-#36B3C3']}
                    label={[
                        'sales',
                        {
                            offset: -2,
                            textStyle: {
                                fill: '#fff',
                                shadowBlur: 2,
                                shadowColor: 'rgba(0, 0, 0, .45)'
                            }
                        }
                    ]}
                    style={{
                        lineWidth: 1,
                        stroke: '#fff'
                    }}
                />
            </Chart>
        );
    }
}
export default Reactjs
