import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Form, Icon, Input, Button, Checkbox } from "antd";
import { Host } from  "../Host";
import axios from "axios";
import firebase from 'firebase';
const FormItem = Form.Item;

class LoginForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      errormessage: '',
      text:'users',
      user_id:'',
      user_name:'',
      user_status:'online',
      u:'',


    };

  }

  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    let msg = '';
//    if (nam === "age") {
//      if (val !="" && !Number(val)) {
//        err = <strong>Your age must be a number</strong>;
//      }
//    }
//    this.setState({errormessage: err});
    this.setState({[nam]: val});
  }


  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
    const username = this.state.username;
    const password= this.state.password;
    if(username == ''){
     return false
    }
    else if(password == ''){
     return false
    }
    else{
        this.LoginAPI();


    }
    });
};

  async userLoginAPI(a, b){
  this.userLoginUpdate(a, b);

 }

  async userLoginUpdate(x , y) {
  var uid = x;
  var una = y;
  firebase.database().ref().child('messages/' + 'users').orderByChild("user_Id").equalTo(uid)
      .on("value", function(snapshot) {
          if (snapshot.val() != null){
              if(Object.values(snapshot.val()).length >= 1){
                snapshot.forEach(function(data) {
                var record = data.val();
                var key = data.key;
                firebase.database().ref().child('messages/' + 'users/'+ key).update({ user_status: "online" });
                });
              }
          }
         else{
            var today = new Date();
            var time = today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds();
            var date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
            var newItem = {
            user_Id:uid,
            user_name:una,
            user_status:'online',
            time:time,
            timestamp : firebase.database.ServerValue.TIMESTAMP
            }
            const usersLoginStatus = firebase.database().ref().child('messages/' + 'users');
            usersLoginStatus.push(newItem);
         }
    });
}


  async LoginAPI(){
    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    var payload={
        "username":this.state.username,
        "password":this.state.password
    }

    try{
     const promise = await axios.post(Host.loginURL+"/login_api", payload);
     this.props.history.push("/workspace");
     const session_data = promise.data.data;
     sessionStorage.setItem("session_data",JSON.stringify(session_data));
     const uid = session_data['user_id'];
     const uname = session_data['username'];
     this.userLoginAPI(uid, uname);
  }
  catch(err){this.setState({msg: "Username & Password is Invalid"});}
//  this.setState({msg: "Username & Password is Invalid"});
}
  render() {
    localStorage.clear();
    const { getFieldDecorator } = this.props.form;
    return (
      <div className="login-form-container">
        <div className="header-login">
          <div className="login-logo">
            <img src="/assets/images/logo-white.png" alt="SQ"/>
          </div>
        </div>
        <div className="login-row">
          <div className="login-cell cell-login-hero" style={{backgroundImage: 'url(/assets/images/login-bg.jpg)'}}>
            <div className="login-cell-block">
              <div className="login-cell-content">
                <div className="login-hero-content">
                  <h1 className="login-heading">Q Nest</h1>
                  <div className="login-subheading">
                    <p>
                    Centralized data platform with insights, visualisation and prediction.
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="login-cell cell-login-form">
            <div className="bg-image-mobile" style={{backgroundImage: 'url(/assets/images/login-bg.jpg)'}}></div>
            <div className="login-cell-block">
              <div className="login-logo text-center">
                <img src="/assets/images/logo-white.png" alt="SQ"/>
              </div>
              <div className="logo-with-form login-cell-content">
                <h4 className="mb-3">Login</h4>
                <Form onSubmit={this.handleSubmit} className="login-form">
                  <FormItem>

                    {getFieldDecorator("userName", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Username!"
                        }
                      ]
                    })(
                      <Input refs="u_name" name="username" onChange={this.myChangeHandler} size="large" prefix={ <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />  }
                        placeholder="Username"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("password", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Password!"
                        }
                      ]
                    })(
                      <Input refs="u_pass" name="password" onChange={this.myChangeHandler}
                        size="large"
                        prefix={
                          <Icon
                            type="lock"
                            style={{ color: "rgba(0,0,0,.25)" }}
                          />
                        }
                        type="password"
                        placeholder="Password"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    <ul className="login-meta">
                      <li>
                        <Link to='/forgot-password'>
                          Forgot password ?
                        </Link>
                      </li>
                      <li>
                        <Checkbox>Remember Me</Checkbox>
                      </li>
                    </ul>
                  </FormItem>
                  <FormItem>
                    <Button
                      type="primary"
                      htmlType="submit"
                      size="large"
                      className="login-form-button"
                      style={{ width: "100%" }}
                    >
                      Log in
                    </Button>
                  </FormItem>
                  <FormItem>
                    <p style={{ color: "red" }}>{this.state.msg}</p>
                  </FormItem>
                </Form>


              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}


const WrappedLoginForm = Form.create()(LoginForm);

export default WrappedLoginForm;
