import React, { Component, useState, useEffect }  from "react";
import { Layout, Menu, Dropdown, Icon, Button, Avatar, Drawer, Input } from "antd";
import AppRouter from "../../router";
import { NotificationTab } from "../components/Header";
import "./PageLayout.scss";
import ReactApexChart from 'react-apexcharts'
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { isAbsolute } from "path";
import * as styles  from "./chat.scss"
import axios from "axios";
import { Host } from  "../../Host";
import firebase from 'firebase';
import SendOutlinedIcon from '@material-ui/icons/SendOutlined';
import TextsmsOutlinedIcon from '@material-ui/icons/TextsmsOutlined';

import { AccessAlarm, ThreeDRotation } from '@material-ui/icons';
import { FirebaseConfig } from  "../../FirebaseConfig";


const stylesIcons = {

  largeIcon: {
    width: 500,
    height: 60,
  },

};



class ChartRoom extends Component {
  constructor(props) {
    super(props);
    this.state = {
        online:false,
        massage_box:false,
        select_username:'',
        user_list:[],
        userName:'Vamsi',
        user_id:'',
        sel_u_id:'',
        send_msg_list:[],
        received_list:[],
        send_id:'',
        received_id:'',
        text:'sqd_massages',
        user_text:'users',
        user_status:[],
        msg:'',
        message:'',
        user_Name:'',
        user_Email:'',
        massage_List:'',
        msg_list:[{'massage':'hi'}],
        x:1,
        u:[],
    }
    this.messageSend = firebase.database().ref().child('messages/' + this.state.text);
    this.allMassageList();

    this.allUserStatus();
  }

componentWillMount() {
    var profile_data = JSON.parse(sessionStorage.getItem("session_data"));
    this.setState({user_Name:profile_data['username']});
    this.setState({user_Email:profile_data['email']});
    this.loadAllUserList()
    this.allMassageList(1);
    this.massageCount()
    this.setState({user_id:profile_data['user_id']});
}


async massageCount(){
this.msg = firebase.database().ref().child('messages/' + this.state.text).on('value', message => {
       if ( message.val() != null){
        var count_list = []
         var c_list = Object.values(message.val());
         var cou = 0;
         var msg_Status='';
         var send_Id = '';
         var received_Id = '';
         for (var i = 0; i < Object.keys(c_list).length; i++) {
              if(this.state.user_id == c_list[i]['received_Id']){
                 if( (c_list[i]['msg_Status'] == false) &&  (2 == c_list[i]['send_Id'])){
                        cou +=1;
                        c_list[i]['count'] = cou;
                        msg_Status = c_list[i]['msg_Status'];
                        send_Id = c_list[i]['send_Id'];
                        received_Id = c_list[i]['received_Id'];



                 }
              }

         }


  }


   });
}










async loadAllUserList()
  {
    this.setState({user_list:[]});
    var profile_data = JSON.parse(sessionStorage.getItem("session_data"));
    const promise = await axios.post(Host.loginURL +"/chat_user_api", {username:profile_data['username'],
                                                                    email:profile_data['email']});
    const status = promise.status;
    if(status===200)
    {
      const users_list= promise.data.users;
      this.setState({user_list:users_list});
    }
  }


selectUser = (username, id) => {
    this.setState({msg_list:[]});
    this.setState({massage_box:true});
    this.setState({select_username:username});
    this.setState({received_id:id});
    this.setState({sel_u_id:id});
    this.allMassageList(id);


  };


  handleSendMassage = (e) => {
     this.setState({massage:this.refs.msg.value})
      var today = new Date();
      var time = today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds();
      var date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
      if(this.state.message != ''){
        var newItem = {
        user_Id:this.state.user_id,
        sender_name:this.state.user_Name,
        receive_name:this.state.select_username,
        send_Id:this.state.user_id,
        received_Id:this.state.received_id,
        message:this.state.message,
        time:time,
        msg_Status:false,
        count:0,
        timestamp : firebase.database.ServerValue.TIMESTAMP
      }
      this.messageSend.push(newItem);
      this.setState({ message: '' });

      }

  }

  handleKeyPress(event) {
    if (event.key !== 'Enter') return;
    this.handleSendMassage();
  }

  handleChange(event) {
    this.setState({message: event.target.value});
  }



async allMassageList(id){
       const s_id = id;
       this.msg = firebase.database().ref().child('messages/' + this.state.text).on('value', message => {
            if ( message.val() != null){
              var i;
              var m_list = []
              var rsm_list = Object.values(message.val());
              for (i = 0; i < Object.keys(rsm_list).length; i++) {
                if((this.state.user_id == rsm_list[i]['received_Id']) || (this.state.user_id == rsm_list[i]['send_Id'])){
                      var re_data = rsm_list[i]
                      m_list.push(re_data)
                }
               this.setState({msg_list:m_list});
              }
            }
            else{
                this.setState({ msg_list:[]});
            }

       });

}


async allUserStatus(){

       this.msg = this.userStatus = firebase.database().ref().child('messages/' + this.state.user_text)
       .on('value', message => {
            if ( message.val() != null){
              var u_list = Object.values(message.val());
              this.setState({user_status:u_list})

              var i;
              var ulist = this.state.user_list;
                for (i = 0; i < Object.keys(ulist).length; i++) {
                 var j;
                 for (j = 0; j< Object.keys(u_list).length; j++) {
                     if(ulist[i]['id'] == u_list[j]['user_Id']){
                        if(u_list[i]['user_status'] == 'online'){
                           ulist[i]['user_status'] = 'online';
                        }
                        else{
                          ulist[i]['user_status'] = 'offline';
                        }

                     }
                     else{
                     }
                 }
              }
           this.setState({u:ulist})


            }
            else{
                this.setState({ user_status:[]});
            }
       });
}


 myFunction = (e) =>{
    var input, filter, ul, p, a, i, txtValue, li, li1;
    input = document.getElementById("Input");
    filter = input.value.toUpperCase();
    ul = document.getElementById("my_search");
    li = ul.getElementsByTagName("div");
    for (i = 0; i < li.length; i++) {
        a = li[i].getElementsByTagName("h5")[0];
        txtValue = a.textContent || a.innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
            li[i].style.display = "";
        } else {
            li[i].style.display = "none";
        }
    }
}




  render() {


    return (
    <div className="messaging">
      <div className="inbox_msg">
        <div className="inbox_people">
              <div className="headind_srch">
                    <div className="recent_heading">
                      <h4>Users</h4>
                    </div>
                    <div className="srch_bar">
                      <div className="stylish-input-group">
                        <input type="text" className="search-bar"  placeholder="Search" id = "Input"
                         onChange={this.myFunction}/ >
                        </div>
                    </div>
              </div>


          <div id="my_search"  className="inbox_chat scroll">
              {this.state.u.map((value, index) =>

                <div  className="chat_list active_chat" id="myUL"  key={index} onClick={()=>this.selectUser(value.username, value.id)}>
                  <div className="chat_people" value={this.state.select_username}>
                        <img src="https://ptetutorials.com/images/user-profile.png" alt="sunil" className="chat_img" />

                         <div>
                             {value.user_status == 'online'?
                                 <div className="chat_ib">
                                  <h5 value={value.id} >{value.username} {value.id}
                                   <span className="online"></span>
                                   </h5>
                                  <p>new user for sq dashboard</p>
                                </div>:

                                <div className="chat_ib">
                                  <h5 value={value.id}>{value.username} {value.id}
                                   <span className="offline"></span>
                                   </h5>
                                  <p>new user for sq dashboard</p>
                                </div>
                             }
                       </div>

                  </div>

                </div>
                )}
            </div>
	  </div>



        <div className="mesgs">
        <div className="alert alert-success  select_user">
          <strong style={{marginLeft:"19px"}}>{this.state.select_username}</strong >
         <img src="https://ptetutorials.com/images/user-profile.png" alt="sunil" className="hear_user" />
          </div>

        {this.state.massage_box == true?
          <div className="msg_history">
                  <div className="incoming_msg">
                         { this.state.msg_list.map((value, index) =>
                              <div className="received_msg">
                              {this.state.sel_u_id == value.send_Id?
                                <div className="received_withd_msg">
                                    <p key={index}>{value.message} </p>
                                  <span className="time_date">{value.sender_name} {value.time}</span>
                                  </div>:null
                                  }
                                  {this.state.sel_u_id == value.received_Id?
                                  <div className="outgoing_msg">
                                        <div className="sent_msg">
                                        <p key={index}>{value.message} </p>
                                        <span className="time_date">{value.sender_name}   {value.time}</span>
                                        </div>
                                  </div>:null
                               }
                              </div>
                          )}
                        </div>

            </div>: <div style={{marginTop:'132px', color:'#bfd0d6'}}>
                       <TextsmsOutlinedIcon style={{fontSize:'100px', marginLeft:'144px',color:'#bfd0d6'}}/>
                       <h2 className="msg_history" style={{color:'#bfd0d6'}}> Welcome Message Chatting</h2>
                     </div>
          }

          <div className="type_msg">
            <div className="input_msg_write">
              <input type="text"  ref='msg' className="write_msg" placeholder="Type a message"
                                              value={this.state.message}
                                              onKeyPress={this.handleKeyPress.bind(this)}
                                              onChange={this.handleChange.bind(this)}/>
                  <button className="msg_send_btn" type="button" onClick={this.handleSendMassage}>
                   <SendOutlinedIcon />
                   </button>
            </div>
          </div>

        </div>
  </div>
</div>

    );
  }
}

export default ChartRoom;
