import React, { Component } from "react";

import { Row, Col, Card, Input, Table, Button, Tag, Divider } from "antd";

import { WrappedAccountForm } from "../components/Form";
const { TabPane } = Tabs;

const columns = [
    {
      title: 'Order Number',
      dataIndex: 'oNumber',
      key: 'oNumber',
    },
    {
      title: 'Confirmation Date',
      dataIndex: 'confDate',
      key: 'confDate',
    },
    {
      title: 'Customer',
      dataIndex: 'customer',
      key: 'customer',
    },
    {
      title: 'Salesperson',
      dataIndex: 'salePer',
      key: 'salePer',
    },
    {
      title: 'Total',
      dataIndex: 'total',
      key: 'total',
    },
    {
      title: 'Invoice Status',
      key: 'invoStatus',
      dataIndex: 'tags',
      render: tags => (
        <span>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </span>
      ),
    },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <span>
          <a>Edit {record.name}</a>
          <Divider type="vertical" />
          <a>Delete</a>
        </span>
      ),
    },
  ];

  const data = [
    {
      key: '1',
      oNumber: 'DVO524',
