class Child2 extends React.Component {
 constructor(props) {
    super(props)
    this.state = {
      sistemas: [],
      fromChild: 'empty',
      x:{id: 1, name: 'Ford', color: 'mokka raveendra'}
    };

  }

  update = (data) => {
    this.setState({fromChild: data})
    this.props.history.push("/test-react");


  }

 render() {
  const { employee: employees } = this.props;
  const { input } = this.state;
  const add = this.state;
  return (
    <div>
      <div className="body">
        {employees
          .filter(employee => employee.name.includes(input))
          .map(employee => {
            <div className='item'>
              <Link className="link" to={`/user/${employee.id}`}>
                <div onClick={() => add(employee)} key={employee.id}>
                  <img className="img"
                    src={`https://picsum.photos/${employee.id}`}
                  />
                </div>
                <h1 className="name2"> {employee.name} </h1>
              </Link>
            </div>
          })}
      </div>
      <div className='input'>
        <input type="text" onChange={(e) => this.setState({ input: e.target.value })} />
      </div>
    </div>
  );
}
}
export default Child2;
