import React, { Component } from "react";
import { Layout, Menu, Dropdown, Icon, Button, Avatar, Drawer} from "antd";
import { Link } from "react-router-dom";
import AppRouter from "../../router";
import { NotificationTab } from "../components/Header";
import "./PageLayout.scss";
import { Host } from  "../../Host";
import axios from "axios";
import firebase from 'firebase';
import ChartRoom from "./chart_room.js"

const { Header, Sider, Content } = Layout;
const { SubMenu } = Menu;



const messageMenu = (
  <Menu>
    <Menu.Item>Message</Menu.Item>
  </Menu>
);

class PageLayout extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapsed: false,
      windowWidth: '',
      windowHeight: '',
      values: [{ name: "John Doe", date: "2017-09-15" }],
      User_Name:'',
     User_Email:'',
     userRights:['Operational', 'Financial','Management']
    }
    this.updateDimensions = this.updateDimensions.bind(this);
  }

componentWillMount() {
     var profile_data = JSON.parse(sessionStorage.getItem("session_data"));
    if(profile_data != null){
        this.setState({User_Name:profile_data['first_name'] + "  " + profile_data['last_name']});
        this.setState({User_Email:profile_data['email']});
        this.setState({user_id:profile_data['user_id']});
    }
  }


static getSelectedPictures = () => {
      return this.state.profile_data;
  }


  updateDimensions() {
    var w = window,
      d = document,
      documentElement = d.documentElement,
      body = d.getElementsByTagName('body')[0],
      width = w.innerWidth || documentElement.clientWidth || body.clientWidth,
      height = w.innerHeight || documentElement.clientHeight || body.clientHeight;
    this.setState({ windowWidth: width, windowHeight: height });
    width <= 991 ? this.setState({ collapsed: true }) : this.setState({ collapsed: false })

  }
  componentDidMount() {
    window.addEventListener("resize", this.updateDimensions);
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.updateDimensions);
  }


  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };

  state = { visible: false };

  showDrawer = () => {
    this.setState({
      visible: true,
    });
  };

  onClose = () => {
    this.setState({
      visible: false,
    });
  };

handleSubmitLogout = (e) => {
    this.userLogout();
};

  async userLogout() {

  firebase.database().ref().child('messages/' + 'users').orderByChild("user_Id").equalTo(this.state.user_id)
      .on("value", function(snapshot) {
          if (snapshot.val() != null){
              if(Object.values(snapshot.val()).length >= 1){
                snapshot.forEach(function(data) {
                var record = data.val();
                var key = data.key;
                var today = new Date();
                var time = today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds();
                var date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
                firebase.database().ref().child('messages/' + 'users/'+ key).update({ user_status: "offline", time:time });
                });
              }
          }
         else{

         }
    });
}



  render() {

  const userMenu = (
  <Menu style={{ minWidth: "260px" }} className="dropdown-menuuser">
    <Menu.Item style={{ padding: "0" }}>
      <div className="usermenu-header">
        <div>
          <div>
            <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
          </div>
        </div>
        <div>
          <h6>{this.state.User_Name}</h6>
          <span>{this.state.User_Email}</span>
        </div>
      </div>
    </Menu.Item>
    <Menu.Item>
      <Link to={{ pathname: '/persoanl-account', data:this.state.profile_data}}> My Account</Link>
    </Menu.Item>
    <Menu.Item>
      <Link to={{ pathname: '/add-user', data:this.state.profile_data}}>Add User</Link>
    </Menu.Item>
    <Menu.Item>
      <Link to="">Company Settings</Link>
    </Menu.Item>
    <Menu.Item>
      <Link to="">
        <Button type="primary" block onClick={this.handleSubmitLogout}>
          Logout
        </Button>
      </Link>
    </Menu.Item>
  </Menu>
);

    return (
      <Layout>
        <Sider
          trigger={null}
          collapsible
          collapsed={this.state.collapsed}
          width={220}
          className="mi-sidebar"
          /* style={{
            overflow: 'auto',
            height: '100vh',
            position: 'fixed',
            left: 0,
          }} */
        >
          <div className="navbar-logo">
            <Link to="/personal-home">
              <img src="/assets/images/logo-white.png" alt="Logo" />
            </Link>
          </div>
          <Menu theme="dark" mode="inline" defaultSelectedKeys={["1"]}>
            <Menu.Item key="1">
              <Link to="/personal-home">
                <Icon type="dashboard" />
                <span>Dashboard</span>
