import React, { Component } from "react";
import { Row, Col, Card, Button, Icon, Progress,Table } from "antd";
import ReactApexChart from 'react-apexcharts';

import ChartBreakdownDonut from "./ChartBreakdownDonut";
import LineChart from "./LineChart";
import BarChart from "./BarChart";


const DataSet = require("@antv/data-set");




    class ManagementAllCharts extends React.Component {

      constructor(props) {
        super(props);

        this.state = {
          area_chart_options: {

          chart: {
              stacked: true,

            },
            colors: ['#008FFB', '#00E396', '#CED4DC'],
            dataLabels: {
              enabled: false
            },
            stroke: {
              curve: 'smooth'
            },
            legend: {
              position: 'top',
              horizontalAlign: 'left'
            },

            fill: {
              type: 'gradient',
              gradient: {
                opacityFrom: 0.6,
                opacityTo: 0.8,
              }
            },
            xaxis: {

              categories: [""],

            },
            tooltip: {
              x: {
                format: 'dd/MM/yy HH:mm'
              },
            }
          },

        area_chart_series:[{
            name: 'series1',
            data: [31, 19, 30]
          }, {
            name: 'series2',
            data: [30, 24, 56]
          }],
        }
      }

      render() {
        return (


          <div id="chart">
            <ReactApexChart options={this.state.area_chart_options} series={this.state.area_chart_series} type="area"  width="600" height="350" />
          </div>


        );
      }
    }
export default ManagementAllCharts;
