class Child2 extends React.Component {
 constructor(props) {
    super(props)
    this.state = {
      sistemas: [],
      fromChild: 'empty',
      x:[{id: 1, name: 'Ravi', color: 'mokka raveendra'},
        {id: 1, name: 'Vamsi', color: 'mokka raveendra'},
         {id: 1, name: 'Raju', color: 'mokka raveendra'},
      {id: 1, name: 'Abu', color: 'mokka raveendra'}],
      y:[{id: 1, name: 'Ravi', color: 'mokka raveendra'},
        {id: 1, name: 'Vamsi', color: 'mokka raveendra'},
         {id: 1, name: 'Raju', color: 'mokka raveendra'},
      {id: 1, name: 'Abu', color: 'mokka raveendra'}],
    };

  }

  update = (data) => {
    this.setState({fromChild: data})
    this.props.history.push("/test-react");


  }

  Change = (e) => {
    if(e.target.value != ''){


     var i;
              var ulist = this.state.x;
                for (i = 0; i < Object.keys(ulist).length; i++) {
                if(ulist[i]['name'] == e.target.value){

                    this.setState({x:[{id: 1, name:e.target.value}]})
                }


                }

    }
    else{
       this.setState({x:this.state.y})
    }


  }



  async myFunction() {
    var input, filter, ul, p, a, i, txtValue, li, li1;
    input = document.getElementById("myInput");
    filter = input.value.toUpperCase();
    ul = document.getElementById("myUL1");
    li = ul.getElementsByTagName("p");
    for (i = 0; i < li.length; i++) {
        a = li[i].getElementsByTagName("h1")[0];
        txtValue = a.textContent || a.innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
            li[i].style.display = "";
        } else {
            li[i].style.display = "none";
        }
    }
}

  render() {


    return (

      <div>
          <input type="text" id="myInput" onChange= {this.myFunction} placeholder="Search for names.." title="Type in a name"/ >
        <div id="myUL">
           {this.state.y.map((value, index) =>
           <div  id="myUL1"className="chat_list active_chat" key={index} onClick={()=>this.selectUser(value.username, value.id)}>
              <div id="myUL2"className="chat_people" >
                   <img src="https://ptetutorials.com/images/user-profile.png" alt="sunil" className="chat_img" />
               <div className="chat_ib" id='val' id="myUL3">
                      <p><h1 href="#">{value.name}
                      <span className="online"></span>
                     </h1></p>
               </div>
             </div>
           </div>

           )}


      </div>
        </div>


    );
  }
}
export default Child2;
