import React, { Component } from "react";
import { Layout, Menu, Dropdown, Icon, Button, Avatar, Drawer} from "antd";
import { Link } from "react-router-dom";
import AppRouter from "../../router";
import { NotificationTab } from "../components/Header";
import "./PageLayout.scss";
import { Host } from  "../../Host";
import axios from "axios";
import ChartRoom from "./chart_room.js"

const { Header, Sider, Content } = Layout;
const { SubMenu } = Menu;



const messageMenu = (
  <Menu>
    <Menu.Item>Message</Menu.Item>
  </Menu>
);

class PageLayout extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapsed: false,
      windowWidth: '',
      windowHeight: '',
      values: [{ name: "John Doe", date: "2017-09-15" }],
      User_Name:'',
     User_Email:'',
    }
    this.updateDimensions = this.updateDimensions.bind(this);
  }

componentWillMount() {
    this.loadManagementHomePageData();
    var profile_data = JSON.parse(sessionStorage.getItem("session_data"));
    this.setState({User_Name:profile_data['first_name'] + "  " + profile_data['last_name']});
    this.setState({User_Email:profile_data['email']});
  }

 async loadManagementHomePageData(){
    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    var options={
        "flog_name":'PageLayout',
       }
    try{
    const response_data = await axios.post(Host.loginURL+"/login_api", options);
    const status = response_data.status;
     if(status===200)
    {
      const profile_data= response_data.data;
      console.log(profile_data);
      this.setState(profile_data)

    }

  }
  catch(err){this.setState({msg: "Username & Password is Invalid"});}
//  this.setState({msg: "Username & Password is Invalid"});
}




static getSelectedPictures = () => {
      return this.state.profile_data;
  }


  updateDimensions() {
    var w = window,
      d = document,
      documentElement = d.documentElement,
      body = d.getElementsByTagName('body')[0],
      width = w.innerWidth || documentElement.clientWidth || body.clientWidth,
      height = w.innerHeight || documentElement.clientHeight || body.clientHeight;
    this.setState({ windowWidth: width, windowHeight: height });
    width <= 991 ? this.setState({ collapsed: true }) : this.setState({ collapsed: false })

  }
  componentDidMount() {
    window.addEventListener("resize", this.updateDimensions);
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.updateDimensions);
  }


  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };

  state = { visible: false };

  showDrawer = () => {
    this.setState({
      visible: true,
    });
  };

  onClose = () => {
    this.setState({
      visible: false,
    });
  };


  render() {

  const userMenu = (
  <Menu style={{ minWidth: "260px" }} className="dropdown-menuuser">
    <Menu.Item style={{ padding: "0" }}>
      <div className="usermenu-header">
        <div>
          <div>
            <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
          </div>
        </div>
        <div>
          <h6>{this.state.User_Name}</h6>
          <span>{this.state.User_Email}</span>
        </div>
      </div>
    </Menu.Item>
    <Menu.Item>
      <Link to={{ pathname: '/persoanl-account', data:this.state.profile_data}}> My Account</Link>
    </Menu.Item>
    <Menu.Item>
      <Link to="">Company Settings</Link>
    </Menu.Item>
    <Menu.Item>
      <Link to="">
        <Button type="primary" block>
          Logout
        </Button>
      </Link>
    </Menu.Item>
  </Menu>
);

    return (
      <Layout>
        <Sider
          trigger={null}
          collapsible
          collapsed={this.state.collapsed}
          width={220}
          className="mi-sidebar"
          /* style={{
            overflow: 'auto',
            height: '100vh',
            position: 'fixed',
            left: 0,
          }} */
        >
          <div className="navbar-logo">
            <Link to="/personal-home">
              <img src="/assets/images/logo-white.png" alt="Logo" />
            </Link>
          </div>
          <Menu theme="dark" mode="inline" defaultSelectedKeys={["1"]}>
            <Menu.Item key="1">
              <Link to="/personal-home">
                <Icon type="dashboard" />
                <span>Dashboard</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="4">
              <Link to="/personal-operations">
                <Icon type="solution" />
                <span>Operations</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="5">
              <Link to="/personal-finance">
                <Icon type="transaction" />
                <span>Finance</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="6">
              <Link to="/personal-hr">
                <Icon type="audit" />
                <span>HR</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="7">
              <Link to="/personal-marketing">
                <Icon type="notification" />
                <span>Marketing</span>
              </Link>
            </Menu.Item>

            <Menu.Item key="8">
              <Link to="/operational-charts">
                <Icon type="solution" />
                <span>Operational</span>
              </Link>
            </Menu.Item>

            <Menu.Item key="9">
              <Link to="/financial-charts">
                <Icon type="transaction" />
                <span>Financial</span>
              </Link>
            </Menu.Item>
             <Menu.Item key="10">
              <Link to="/management-charts">
                <Icon type="home" />
                <span>Management</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="11">
              <Link to="/personal-source">
                <Icon type="project" />
                <span>Source</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="11">
              <Link to="/personal-documents">
                <Icon type="file" />
                <span>Documents</span>
              </Link>
            </Menu.Item>
            <SubMenu
              key="sub4"
              title={
                <span>
                  <Icon type="setting" />
                  <span>Settings</span>
                </span>
              }
            >
              <Menu.Item key="10">
                <Link to='/persoanl-account'>My Account</Link>
              </Menu.Item>
              <Menu.Item key="11">Company Settings</Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout /* style={{ marginLeft: 220 }} */>
          <Header className="mi-header" style={{ /* position: 'fixed', zIndex: 1, width: '100%', */ padding: 0 }}>
            <div className="header-block">
              <span className="header-trigger">
                <Icon
                  className="trigger"
                  type={this.state.collapsed ? "menu-unfold" : "menu-fold"}
                  onClick={this.toggle}
                />
              </span>
              <ul className="nav-actions">
                <li>
                  <Dropdown
                    overlay={<NotificationTab />}
                    placement="bottomRight"
                    trigger={["click"]}
                    className="dropdown-notification"
                  >
                    <a className="ant-dropdown-link d-dropdown-link" href="#">
                      <Icon type="bell" />
                    </a>
                  </Dropdown>
                </li>
                <li>
                  <Dropdown
                    overlay={userMenu}
                    placement="bottomRight"
                    trigger={["click"]}
                  >
                    <a className="ant-dropdown-link d-dropdown-link" href="#">
                      <Icon type="user" />
                    </a>
                  </Dropdown>
                </li>
               <li>
                    <a className="ant-dropdown-link d-dropdown-link" href="#" onClick={this.showDrawer}>
                      <Icon type="message" />
                    </a>
                  <Drawer
                    title="Message"
                    placement="right"
                    closable={false}
                    onClose={this.onClose}
                    visible={this.state.visible}
                    width="800px"
                    className="drawer-chat"
                  >
                      <ChartRoom />




                    {/* <div className="chat-lists">
                      <div className="chat-item chat-left">
                        <div className="chat-media">
                          <div className="chat-media-icon">
                            <Icon type="user" />
                          </div>
                        </div>
                        <div className="chat-denote">
                          <div className="chat-message">
                            <div className="chat-text">
                              Hi, Received Msg
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="chat-item chat-right">
                        <div className="chat-media">
                          <div className="chat-media-icon">
                            <Icon type="user" />
                          </div>
                        </div>
                        <div className="chat-denote">
                          <div className="chat-message">
                            <div className="chat-text">
                              Hello, Send Msg
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="chat-item chat-right">
                        <div className="chat-media">
                          <div className="chat-media-icon">
                            <Icon type="user" />
                          </div>
                        </div>
                        <div className="chat-denote">
                          <div className="chat-message">
                            <div className="chat-text">
                              {this.state.sendMsg}
                            </div>
                          </div>
                        </div>
                      </div>


                    </div>
                    <div className="chat-input-wrap">
                      <div className="chat-input-group">
                        <Input size="large" placeholder="Type your message here..." />
                        <span className="chat-input-appended">
                          <Button type="primary" className="btn-chat-send" onClick={this.sendMassage}>
                            <img src="assets/images/paper-plane.png" alt=""/>
                          </Button>
                        </span>
                      </div>
                    </div>  */}


                  </Drawer>
                </li>
              </ul>
            </div>
          </Header>
          <Content
            style={{
              padding: 40,
              minHeight: 280
            }}
          >
            <AppRouter />



          </Content>
        </Layout>
      </Layout>

    );
  }
}

export default PageLayout;
