import React, { Component } from "react";

import { Row, Col, Card, Button, Icon, Progress } from "antd";
import ReactApexChart from 'react-apexcharts'
import ChartHistoryGroupColumn from "../components/charts/ChartHistoryGroupColumn";
const DataSet = require("@antv/data-set");



class Reactjs extends Component {

      constructor(props) {
        super(props);

        this.state = {
         options : {
            plotOptions: {
              bar: {
                horizontal: true,
                dataLabels: {
                  position: 'top',
                },
              }
            },
            dataLabels: {
              enabled: true,
              offsetX: -6,
              style: {
                fontSize: '12px',
                colors: ['#fff']
              }
            },
            stroke: {
              show: true,
              width: 1,
              colors: ['#fff']
            },
            series2: [{
                name: 'Marine Sprite',
                data: [2000, 3000]
            },{
                name: 'Reborn Kid',
                data: [3000, 40000]
            }],
            title: {
                text: ''
            },
            xaxis: {
                categories: [2008, 2009, 2010, 2011, 2012, 2013, 2014],
                labels: {
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                }
            },

            tooltip: {
                    y: {
                        formatter: function(val) {
                        return val + "K"
                    }
                }
            },


            legend: {
                position: 'top',
                horizontalAlign: 'left',
                offsetX: 40
            }
        },
          series: [{
            data: [2000000,3000000,4000000,5000000,6000000,7000000,8000000]
          }],
        }
      }

      render() {
        return (


          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height="350" />
          </div>


        );
      }
    }

export default Reactjs;
