  async loadCheckbox(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }

    const promise = await axios.post("http://127.0.0.1:8070/api/v1/filter_api", options);
