import * as React from 'react';
import * as ReactDOM from 'react-dom';

import { DropDownList } from '@progress/kendo-react-dropdowns';
import { countries } from './countries';

class Test extends React.Component {
    state = {
        success: false
    };

    render() {
        const departureValidationMessage = 'Please select a departure country!';
        const arrivalValidationMessage = 'Please select an arrival country!';

        return (
            <div className="row example-wrapper">
                <div className="col-xs-12 col-sm-6 offset-sm-3 example-col">
                    <div className="card">
                        <div className="card-block">
                            <form className="k-form" onSubmit={this.handleSubmit}>
                                <fieldset>
                                    <legend>Select your flight route:</legend>
                                    <div className="mb-3">
                                        <DropDownList
                                            style={{ width: '100%' }}
                                            label="Departure Country"
                                            name="departureCountry"
                                            data={countries}
                                            required={true}
                                            validationMessage={departureValidationMessage}
                                        />
                                    </div>
                                    <div>
                                        <DropDownList
                                            style={{ width: '100%' }}
                                            label="Arrival Country"
                                            name="arrivalCountry"
                                            data={countries}
                                            required={true}
                                            validationMessage={arrivalValidationMessage}
                                        />
                                    </div>
                                </fieldset>
                                <input type="submit" className="k-button k-primary" value="Search" />
                            </form>
                        </div>
                    </div>
                </div>
                {this.state.success && (
                    <div
                        className="alert alert-success"
                        style={{ position: 'absolute' }}
                    >
                        Form submitted!
                    </div>)}
            </div>
        );
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.setState({ success: true });
        setTimeout(() => { this.setState({ success: false }); }, 3000);
    }
}

export default Test;
