import { Chart, Tooltip, Axis, Legend, Pie, Coord } from 'viser-react';
import * as React from 'react';
import { Row, Col, Card, Button, Icon, Progress } from "antd";
const DataSet = require('@antv/data-set');

const sourceData = [
  { item: '事例一', count: 40 },
  { item: '事例二', count: 21 },
  { item: '事例三', count: 17 },
  { item: '事例四', count: 13 },
  { item: '事例五', count: 9 }
];

const scale = [{
  dataKey: 'percent',
  min: 0,
  formatter: '.0%',
}];

const dv = new DataSet.View().source(sourceData);
dv.transform({
  type: 'percent',
  field: 'count',
  dimension: 'item',
  as: 'percent'
});
const data = dv.rows;

class Reactjs extends React.Component {

 constructor(props) {
    super(props);
   this.state = {

    books:[],
     intHeader:[],
     filter:[],
     chart_data:[],
     donut_data:[],
     pie_data:[],
     l:[]

      }
   }

   handleSubmitDonut = (e) => {
    e.preventDefault();
    this.setState({value: e.target.value});
     this.DonutCheckbox(
         {Column:e.target.value }
  );
  };
async DonutCheckbox(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }


    const promise = await axios.post("http://127.0.0.1:8070/api/v1/count_api", options);
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      this.setState(data);
      console.log(data);
      var i;
      var keys_list =[];
      var value_list =[];

      var chart_list = []

      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]

            keys_list.push(key)
            if (key != "label"){
            ct_list1.push(key)

            }
            else{}

            var value = data[key]
            if (this.state.value != value){value_list.push(value)}
            else {
            }

            var data_dict = {};
            data_dict['name']=key;
            data_dict['data']=[value];
            chart_list.push(data_dict)
        }

      const d_data = [this.state.SQBL, this.state.SQBL-1, this.state.SQBL-2, this.state.SQCL, this.state.SQCL-1,
                       this.state.SQBL-2]
      this.setState({donut_data:value_list})

    }

}


  render() {
    return (

      <Chart forceFit height={400} data={data} scale={scale}>
        <Tooltip showTitle={false} />
        <Coord type="theta" />
        <Axis />
        <Legend dataKey="item" />
        <select class="custom-select"  value={this.state.value} onChange={this.handleSubmitDonut}
                    style={{ width:"110px", marginLeft:"20px" }}>
                     <option  value="" >Xaxis</option>
                     {this.state.books.map((value,index)=>
                              {return   <option class="btn btn-custom btn-block btn-detail"
                              style={{ cursor: "pointer;", fontSize: "14px"}} key={index}> {value} </option> }
                              )}


                             </select>

        <Pie
          position="percent"
          color="item"
          style={{ stroke: '#fff', lineWidth: 1 }}
          label={['percent', {
            formatter: (val, item) => {
              return item.point.item + ': ' + val;
            }
          }]}
        />
      </Chart>

    );
  }
}





export default Reactjs;
