import { Chart, Legend, Axis, Tooltip, StackInterval } from 'viser-react';
import * as React from 'react';
const DataSet = require('@antv/data-set');

const data = [{
    country: 'A',
    year: '1750',
    value: 1000000
}, {
    country: 'B',
    year: '1800',
    value: 635
}, {
    country: 'C',
    year: '1850',
    value: 8
},];




const ds = new DataSet();
const dv = ds.createView('demo').source(data).transform({
    type: 'percent',
    field: 'value', // 统计销量
    dimension: 'country', // 每年的占比
    groupBy: ['year'], // 以不同产品类别为分组
    as: 'percent'
});

const scale = [
    {
        dataKey: 'percent',
        min: 0,
        formatter: function formatter(val) {
            return val + 'M';
        }
    }
];
class Reactjs extends React.Component {

    render() {

        return (
            <Chart forceFit height={500} data={dv} scale={scale} padding={['auto']}>
                <Legend dataKey="country" />
                <Tooltip />
                <Axis dataKey="year" />
                <StackInterval
                    position="year*percent"

                    color={[
                        'country',
                        ['#FF6A84', '#30A4EB', '#45BFC0', '#FFCC55']
                    ]}
                    shape="top-line"
                />
            </Chart>
        );
    }
}
export default Reactjs;
