import React, { Component } from "react";
import { Row, Col, Card, Avatar, Tabs, Form, Icon, Input, Button, Checkbox } from "antd";
import { WrappedAccountForm } from "../components/Form";
import { PageLayout } from  "../Layout/PageLayout";
import { Host } from  "../../Host";
import swal from 'sweetalert';
import axios from "axios";
const { TabPane } = Tabs;





class AccountSetting extends Component {
    constructor(props) {
    super(props);
    this.state = {
     userName:'',
     userId:'',
     errorMsg:'',
     Account_type:''
    }
 }

componentWillMount() {
    const profile_data = JSON.parse(sessionStorage.getItem("session_data"));
    this.setState({User_Name:profile_data['first_name'] + "  " + profile_data['last_name']});
    this.setState({userId:profile_data['user_id']});
    this.setState({userName:profile_data['username']});
}

 handleSubmitChangePwd = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
     const password = e.target.pwd.value;
     const new_pwd = e.target.npwd.value;
     const conf_pwd = e.target.cpwd.value;
      if(password == ''){
     return false
    }
    else if(new_pwd == ''){
     return false
    }
    else if(conf_pwd == ''){
     return false
    }
    else if(new_pwd != conf_pwd){
     this.setState({errorMsg:'NewPassword and ConfirmPassword does not match'})
    }

    else{
       const req_data = {'userId':this.state.userId,'userName':this.state.userName,
                         'password':new_pwd, 'flog_value':'change password'}
     this.ChangePasswordAPI(req_data);
    }
   });
};

 async ChangePasswordAPI(req_data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(req_data),
    }
    try{
     const promise = await axios.post(Host.loginURL+"/signup_api", options);
     const status = promise.status;
     if(status==200)
    {
      const data = promise.data.data;
      swal({
            title:"",
            text:'Your password has been changed successfully!',
            icon: "success",
        })
        .then((willDelete) => {
         this.props.history.push("/");
        });

     }
  }
  catch(err){this.setState({msg: "Your Password Not Changed"});}
}

  render() {
        const formItemLayout = {
            labelCol: {
            xs: { span: 24 },
            sm: { span: 8 },
            },
            wrapperCol: {
            xs: { span: 24 },
            sm: { span: 16 },
            },
        };
        const tailFormItemLayout = {
            wrapperCol: {
            xs: {
                span: 24,
                offset: 0,
            },
            sm: {
                span: 16,
                offset: 8,
            },
            },
        };

        const { getFieldDecorator } = this.props.form;
        const profile_data = JSON.parse(sessionStorage.getItem("session_data"));
        const data = this.props.location.data;

    return (
      <div>
        <div className="page-header">
          <Row type="flex" justify="space-between">
            <Col>
              <h1 className="page-heading">My Account</h1>
            </Col>
          </Row>
        </div>
        <div id="content">
          <Row gutter={24} type="flex">
            <Col span={8} className="c-mb">
              <Card
                bordered={false}
                extra={
                  <div className="card-userinfo-header">
                    <div>
                      <div>
                        <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                      </div>
                    </div>
                    <div>
                      <h5 className="username">{this.state.User_Name}</h5>
                      <span>{this.state.User_Email}</span>
                    </div>
                  </div>
                }
                className="mi-card mi-card-boxshadow mi-card-userinfo"
              >
                <div>
                  <table className="userinfo-table">
                    <tbody>
                      <tr>
                        <td>Account type</td>
                        <td>: {profile_data['account_type']}</td>
                      </tr>
                      <tr>
                        <td>Company</td>
                        <td>: {profile_data['company']}</td>
                      </tr>
                      <tr>
                        <td>Title</td>
                        <td>: {profile_data['title']}</td>
                      </tr>
                      <tr>
                        <td>Office number</td>
                        <td>: {profile_data['office_number']}</td>
                      </tr>
                      <tr>
                        <td>Cell number</td>
                        <td>: {profile_data['cell_number']}</td>
                      </tr>
                      <tr>
                        <td>Home number</td>
                        <td>: {profile_data['home_number']}</td>
                      </tr>
                      <tr>
                        <td>Email</td>
                        <td>: {profile_data['email']}</td>
                      </tr>
                      <tr>
                        <td>Address</td>
                        <td>
                          : {profile_data['address']}
                        </td>
                      </tr>


                      <tr>
                        <td>Address123</td>
                        <td>
                          : {this.props.data}
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </Card>
            </Col>

            <Col span={16} className="c-mb">
              <Card
                bordered={false}
                className="mi-card mi-card-boxshadow mi-card-user-setting"
              >
                <Tabs defaultActiveKey="1">
                  <TabPane tab="Profile" key="1">
                    <div className="tabpane-inside-gutters">
                      <Row type="flex" justify="">
                        <Col span={16}>
                          <WrappedAccountForm />
                        </Col>
                      </Row>
                    </div>
                  </TabPane>
                  <TabPane tab="Change Password" key="2">
                    <div className="tabpane-inside-gutters">
                    <Row type="flex" justify="">
                        <Col span={16}>
                 <Form {...formItemLayout}  onSubmit={this.handleSubmitChangePwd}>
                        <Form.Item label="CurrentPassword">
                            {getFieldDecorator('pwd', {rules:[{required: true,
                            message: "Please input your CurrentPassword!"}]} )
                            (<Input type="password" name="password" />)}
                        </Form.Item>
                        <Form.Item label="NewPassword">
                            {getFieldDecorator('npwd', {rules:[{required: true,
                            message: "Please input your New Password!"}]}
                            )(<Input  type="password" name="new password"/>)}
                        </Form.Item>
                        <Form.Item label="ConfirmPassword">
                            {getFieldDecorator('cpwd', {rules:[{required: true,
                            message: "Please input your Confirm Password!"}]}
                            )(<Input  type="password" name="confirm password"/>)}
                        </Form.Item>
                            <p style={{ color: "red", textAlign:'center' }}>{this.state.errorMsg}</p>
                         <Form.Item {...tailFormItemLayout}>
                            <Button type="primary" htmlType="submit">
                                Save
                            </Button>
                        </Form.Item>

                  </Form>
                        </Col>
                    </Row>
                    </div>
                  </TabPane>
                  <TabPane tab="Notifications" key="3">
                    <div className="tabpane-inside-gutters"></div>
                  </TabPane>
                </Tabs>
              </Card>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
const WrappedAccountSetting = Form.create()(AccountSetting);
export default WrappedAccountSetting;
