import React, { Component } from "react";
import {Link, Switch, Route } from "react-router-dom";
import { Row, Col, Card,
         Button, Layout,Input,
         Menu, Breadcrumb,
         Icon, Tree, Checkbox,
         Modal, Select, Form} from 'antd';
import PlaneChart from './chart';
import LineChart from './LineChart';
import BarChart from './BarChart';
const { Option } = Select;




class ChartDashboard extends React.Component {
 constructor(props) {
    super(props)
    this.state = {
       ModalText: 'Content of the modal',
       visible: false,
       confirmLoading: false,
       loading: false,
       line:false,
       DashboardChart:PlaneChart

    };
 }
  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = () => {

    this.setState({ loading: true });
    this.setState({
      ModalText: 'The modal will be closed after two seconds',
      confirmLoading: true,
    });
    setTimeout(() => {
      this.setState({
         loading: false,
        visible: false,
        confirmLoading: false,
      });
    }, 2000);
  };

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  };

onChange = (value) => {
  console.log(`selected ${value}`);
}

onBlur = () => {
    console.log('blur');
}


onFocus = () => {
  console.log('focus');
}

onSearch = (val) => {
     console.log('search:', val);
}

handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        const chatName = values["chatName"]
         if (chatName == 'line') {this.setState({DashboardChart:LineChart})}
         if (chatName == 'bar') {this.setState({DashboardChart:BarChart})}



      }
      else{
            console.log(e.target.value,)
      }
    });
  };



  render() {

      const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };

    const { getFieldDecorator } = this.props.form;
    return (

     <div>
              <div className="page-header">
                  <Row type="flex" justify="space-between">
                    <Col>
                      <h1 className="page-heading"></h1>
                    </Col>
                    <Col>
                      <ul className="button-actions">
                        <li>
                          <div>
                            <Button type="primary" onClick={this.showModal}>
                              Create Dashboard
                            </Button>
                               <Modal
                                  title="Add New Chart"
                                  visible={this.state.visible}
                                  onOk={this.handleOk}
                                  confirmLoading={this.state.confirmLoading}
                                  onCancel={this.handleCancel}
                                  footer={[
                                    <Button key="back" onClick={this.handleCancel}>
                                      Cancel
                                    </Button>,
                                    <Button key="submit" type="primary"
                                       loading={this.state.loading} onClick={this.handleSubmit}>
                                      Add Chart
                                    </Button>,
                                  ]}
                                  >
                                     <Form {...formItemLayout} onSubmit={this.handleSubmit}>
                                       <Form.Item label="Chart Type">
                                        {getFieldDecorator('chatName', {rules:[{required: true,
                                           message: "Please Select Chart type !"}]} )
                                          (<Select
                                            showSearch
                                            style={{ width: 200 }}
                                            placeholder="Select a option"
                                            optionFilterProp="children"
                                            onChange={this.onChange}
                                            onFocus={this.onFocus}
                                            onBlur={this.onBlur}
                                            onSearch={this.onSearch}
                                            filterOption={(input, option) =>
                                              option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                            }
                                          >
                                            <Option value="line"><Icon type="line-chart" /> Line Chart</Option>
                                            <Option value="bar"><Icon type="bar-chart" /> Bar Chart</Option>
                                            <Option value="vertical"><Icon type="bar-chart" />VerticalBar Chart</Option>
                                            <Option value="group"><Icon type="bar-chart" /> GroupBar Chart</Option>
                                            <Option value="pie"><Icon type="pie-chart" /> Pie Chart</Option>
                                            <Option value="area"><Icon type="area-chart" /> Area Chart</Option>
                                         </Select>)}
                                     </Form.Item>
                                    </Form>

                                </Modal>
                          </div>
                        </li>
                        <li>
                          <a href="">
                            <Button type="primary" ghost>
                              Save Dashboard
                            </Button>
                          </a>
                        </li>
                      </ul>
                    </Col>
                  </Row>
                </div>
          <Layout>

               <this.state.DashboardChart/>




          </Layout>,

    </div>


    );
  }
}
const WrappedChartDashboard = Form.create()(ChartDashboard);
export default WrappedChartDashboard;
