import { Chart, Tooltip, Axis, Legend, Pie, Coord, Line, Bar, Point} from 'viser-react';
import * as React from 'react';
import { Row, Col, Card, Button, Icon, Progress, Form , Modal, Table, Select, DatePicker} from "antd";
import { Host } from  "../../Host";
import axios from "axios";
import ReactApexChart from 'react-apexcharts'
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { CSVLink } from "react-csv";
import { css } from "@emotion/core";
import { RingLoader} from "react-spinners";
const DataSet = require('@antv/data-set');
const Menu_name = "Management";
const { Option } = Select;
const { MonthPicker, RangePicker, WeekPicker } = DatePicker;


const vertical_scale = [{
  dataKey: 'value',
  min: 0,
  formatter: function formatter(val) {
             if (val < 100000) {
                  return Math.round((val/1000) * 10)/10+'k';
                    }
            else if (val >= 1000000){

            return val=(val/1000000)+"M";

            }
             else {
                      return val;
                    }
  }
},{
  dataKey: 'year',
  min: 0,
  max: 1,
}];

class VerticalChart extends React.Component {

 constructor(props) {
    super(props);
   this.state = {
      Headers:[],
     intHeader:[],
     filter:[],
     start_date:'',
     end_date:'',
     select_unit:[],
     select_unit_list:'',
     select_group:"",
     select_amount_USD:"Amount_USD",
     chart_data:[],
     donut_data:[],
     pie_data:[],
     bar_data:[],
     ve_bar_data:[],
     line_data:[{name:"", 'A':1, 'B':12}],
     field_list:["A","B"],
     chartData: [],
     sourceData :[],
     line_chart_source_data:[{name: 'A', value: 13000000}],
     sourceData_donut:[],
     value_v_bar : "Status",
     value_yl : "Amount_USD",

     table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     table_data : [{ key: '1', oNumber: 'DVO524',}],

     vertical_table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     vertical_table_data : [{ key: '1', oNumber: 'DVO524',}],

     download_verticalChart_table :[{ firstname: "Ravi", lastname: "Tomi", email: "ah@smthing.co.com" }],

      }
}


// ============================= Loading all charts ====================================================================

componentWillMount() {
    this.loadAllHeaders();
     this.loadIntHeaders();
     this.loadAll_count_view();
  }

 async loadAllHeaders()
  {
    const promise = await axios.post(Host.loginURL +"/header_api", {Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);
      this.setState({Headers:data});
    }
  }

async loadIntHeaders()
  {
    const promise = await axios.post(Host.loginURL +"/int_Header_api", {Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);
      this.setState({intHeader:data});
      const d = [{'name': 'Actual','data': []}, {'name': 'Budget','data': []}]
      const dou = [1,3,4]
      this.setState({donut_data:dou})
      this.setState({chart_data:d})
       console.log(this.state.chart_data);

    }
  }

async loadAll_count_view()
  {
    this.setState({table_columns : []})
    this.setState({table_data : []})

    this.setState({download_barChart_table:[]})
    this.setState({download_verticalChart_table:[]})
    this.setState({download_donutChart_table:[]})
    this.setState({download_pieChart_table:[]})
    const promise = await axios.post(Host.loginURL +"/load_count_api", {Column:'Status', Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      var table_dict = data
      var i;
      var keys_list =[];
      var bar_value_list =[];
      var load_chart_list = [];
      var load_donut_chart_list = [];
      var ct_list1 = [];
      var table_column_list = [];
      var table_data_list = [];
      var table_data_dict = {}
      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]


            if (key != "label"){
            ct_list1.push(key)
            keys_list.push(key)

            }
            else{}

            var value = data[key]
            if (this.state.value_bar != value){bar_value_list.push(value)}
            else {
            }

            var data_dict = {};

            var load_data_dict = {};
            if (key != "label"){
            data_dict['name']=key;
            load_data_dict['item']=key;
            data_dict['value']=value;
            load_data_dict['count']=value;
            load_chart_list.push(data_dict)
            load_donut_chart_list.push(load_data_dict)
            var column_dict = {}
            column_dict['title'] =key;
            column_dict['dataIndex'] =key;
            column_dict['key'] =key;
            table_data_dict[key] = value;
            table_column_list.push(column_dict)
            }
            else{}
        }
      table_data_list.push(table_data_dict)
      this.setState({select_unit_list:keys_list})

      this.setState({bar_data:load_chart_list})
      this.setState({ve_bar_data:load_chart_list})
      this.setState({sourceData:load_donut_chart_list})
      this.setState({sourceData_donut:load_donut_chart_list})

      this.setState({bar_table_columns:table_column_list})
      this.setState({bar_table_data:table_data_list})

      this.setState({vertical_table_columns:table_column_list})
      this.setState({vertical_table_data:table_data_list})

      this.setState({donut_table_columns:table_column_list})
      this.setState({donut_table_data:table_data_list})

      this.setState({pie_table_columns:table_column_list})
      this.setState({pie_table_data:table_data_list})

     this.setState({download_barChart_table:[table_dict]})
    this.setState({download_verticalChart_table:[table_dict]})
    this.setState({download_donutChart_table:[table_dict]})
    this.setState({download_pieChart_table:[table_dict]})
    this.setState({loading:false})

    }
  }



//========================================== Vertical bar Chart ================================================================
onBlur = () => {
    console.log('blur');
}


onFocus = () => {
  console.log('focus');
}

onSearch = (val) => {
     console.log('search:', val);
}


onChangeDate = (date, dateString) => {
  console.log(date, dateString);
  this.setState({start_date:dateString[0]})
  this.setState({end_date:dateString[1]})
}


handleChange = (value) => {
console.log(value)
 this.setState({select_unit:value})
  this.setState({value})
  this.VerticalBarChartData({Column:this.state.value_v_bar, start_date:this.state.start_date, end_date:this.state.end_date,
                            unit_value:value, Menu:Menu_name});
}


onChange_Xaxis= (value) => {
  this.setState({value_v_bar:""})
  this.setState({value_v_bar:value});
  this.VerticalBarChartData({Column:value, start_date:this.state.start_date, end_date:this.state.end_date,
                            unit_value:this.state.select_unit, Menu:Menu_name});
}

async VerticalBarChartData(data){
    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }

    this.setState({ve_bar_data : []})

    this.setState({vertical_table_columns:[]})
      this.setState({vertical_table_data:[]})


 this.setState({download_verticalChart_table:[]})
    const promise = await axios.post(Host.loginURL +"/count_api", options);
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      var table_dict = data;

      this.setState(data);
      console.log(data);
      var i;
      var keys_list =[];
      var bar_value_list =[];
      var Ve_Bar_chart_list = []
      var ct_list1 = []

      var table_column_list = [];
      var table_data_list = [];
      var table_data_dict = {}
      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]


            if (key != "label"){
            ct_list1.push(key)
             keys_list.push(key)

            }
            else{}

            var value = data[key]
            if (this.state.value_bar != value){bar_value_list.push(value)}
            else {
            }

            var data_dict = {};
            if (key != "label"){
            data_dict['name']=key;
            data_dict['value']=value;
            Ve_Bar_chart_list.push(data_dict)

            var column_dict = {}
            column_dict['title'] =key;
            column_dict['dataIndex'] =key;
            column_dict['key'] =key;
            table_data_dict[key] = value;
            table_column_list.push(column_dict)



            }
            else{}
        }
     table_data_list.push(table_data_dict)
     this.setState({select_unit_list:keys_list})

     this.setState({ve_bar_data:Ve_Bar_chart_list})
     this.setState({vertical_table_columns:table_column_list})
     this.setState({vertical_table_data:table_data_list})

     this.setState({download_verticalChart_table:[table_dict]})
      this.setState({loading:false})

    }

}

render() {

const children = [];
var x = this.state.select_unit_list
for (let i = 0; i < x.length; i++) {
  children.push(<Option key={x[i]}>{x[i]}</Option>);
}


const ve_bar_dv = new DataSet.View().source(this.state.ve_bar_data);
ve_bar_dv.transform({
  type: 'sort',
  callback(a, b) {
    return a.value - b.value > 0;
  },
});
const vertical_bar_data = ve_bar_dv.rows;

const override = css`
  display: block;
  margin: 0 auto;
  border-color: red;
  loading: true;
  color: "red";
  css: "";
`;


    return (

<Card
    title={this.state.value_v_bar}
    style={{ minHeight: "100%" }}
    bordered={false}
    className="mi-card mi-card-header-borderless mi-card-boxshadow" >
   <div>
       <div id="chart">
            <Select
                        showSearch
                        style={{ width: 100 }}
                        defaultValue="Status"
                        placeholder="Select a option"
                        optionFilterProp="children"
                        onChange={this.onChange_Xaxis}
                        onFocus={this.onFocus}
                        onBlur={this.onBlur}
                        onSearch={this.onSearch}
                        filterOption={(input, option) =>
                          option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                      >
                {this.state.Headers.map((value,index)=>
                <Option value={value} key={index}>{value}</Option>
                )}
             </Select>

             <RangePicker style={{ marginLeft:'5px' }} onChange={this.onChangeDate} />

              <Select
                    mode="multiple"
                    style={{ width: '12%', marginLeft:'5px' }}
                    placeholder="Select Unit"
                    defaultValue={[]}
                    onChange={this.handleChange}
                  >
                    {children}
             </Select>,
       <Button type="primary" data-toggle="modal" data-target="#myModal_Vertical"
        style={{marginLeft:"10px"}}> <Icon type="table"  style={{fontSize:'25px'}}/> </Button>
          <div class="modal fade" id="myModal_Vertical" role="dialog">
            <div class="modal-dialog">
              <div class="modal-content">
                <div class="modal-header">
                 {/* <button type="button" class="close" data-dismiss="modal" style={{marginLeft:"1px"}}>&times;</button> */}
                  <h4 class="modal-title">Vertical Bar Chart Table Data</h4>
                </div>
                <div class="modal-body">
                 <Table columns={this.state.vertical_table_columns} dataSource={this.state.vertical_table_data} style={{overflowY:"scroll"}}/>

                </div>
                <div class="modal-footer">
                  <Button type="primary" ghost data-dismiss="modal">Close</Button>
                  <CSVLink data={this.state.download_verticalChart_table}>
                  <Button type="primary" ><Icon type="download" /> Download </Button>
                  </CSVLink>;
                </div>
              </div>

            </div>
          </div>

           <RingLoader
                  css={override}
                  size={150} // or 150px
                  color={"#123abc"}
                  loading={this.state.loading}
              />
            <Chart forceFit width={800} height={400} data={vertical_bar_data} scale={vertical_scale}>
            <Tooltip />
            <Axis />
            <Bar position="name*value" />
            </Chart>

    </div>

  </div>
</Card>

    );
  }
}
export default VerticalChart;
