import React, { Component } from "react";
import {Link, Switch, Route } from "react-router-dom";
import { Row, Col, Card,
         Button, Layout,
         Menu, Breadcrumb,
         Icon, Tree, Checkbox,
         Modal, Select} from 'antd';
import LineChart from './LineChart';
const { Option } = Select;


class ChartDashboard extends React.Component {
 constructor(props) {
    super(props)
    this.state = {
       ModalText: 'Content of the modal',
       visible: false,
       confirmLoading: false,

    };
 }
  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = () => {
    this.setState({
      ModalText: 'The modal will be closed after two seconds',
      confirmLoading: true,
    });
    setTimeout(() => {
      this.setState({
        visible: false,
        confirmLoading: false,
      });
    }, 2000);
  };

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  };

onChange = (value) => {
  console.log(`selected ${value}`);
}

onBlur = () => {
    console.log('blur');
}


onFocus = () => {
  console.log('focus');
}

onSearch = (val) => {
     console.log('search:', val);
}


  render() {
    return (

     <div>
              <div className="page-header">
                  <Row type="flex" justify="space-between">
                    <Col>
                      <h1 className="page-heading"></h1>
                    </Col>
                    <Col>
                      <ul className="button-actions">
                        <li>
                          <div>
                            <Button type="primary" onClick={this.showModal}>
                              Create Dashboard
                            </Button>
                               <Modal
                                  title="Add New Chart"
                                  visible={this.state.visible}
                                  onOk={this.handleOk}
                                  confirmLoading={this.state.confirmLoading}
                                  onCancel={this.handleCancel}
                                  >
                                      <Select
                                            showSearch
                                            style={{ width: 300 }}
                                            placeholder="Select a option"
                                            optionFilterProp="children"
                                            onChange={this.onChange}
                                            onFocus={this.onFocus}
                                            onBlur={this.onBlur}
                                            onSearch={this.onSearch}
                                            filterOption={(input, option) =>
                                              option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                            }
                                          >
                                            <Option value="jack"><Icon type="line-chart" /></Option>
                                            <Option value="lucy"><Icon type="bar-chart" /></Option>
                                            <Option value="tom"><Icon type="-chart" /></Option>
                                      </Select>,
                                </Modal>
                          </div>
                        </li>
                        <li>
                          <a href="">
                            <Button type="primary" ghost>
                              Save Dashboard
                            </Button>
                          </a>
                        </li>
                      </ul>
                    </Col>
                  </Row>
                </div>
          <Layout>
              <LineChart />
          </Layout>,

    </div>


    );
  }
}
export default ChartDashboard;
