import { Chart, Tooltip, Axis, Legend, Pie, Coord, Line, Bar, Point} from 'viser-react';
import * as React from 'react';
import { Row, Col, Card, Button, Icon, Progress, Form , Modal, Table, Select, DatePicker} from "antd";
import { Host } from  "../../Host";
import axios from "axios";
import ReactApexChart from 'react-apexcharts'
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { CSVLink } from "react-csv";
import { css } from "@emotion/core";
import { RingLoader} from "react-spinners";
const DataSet = require('@antv/data-set');
const Menu_name = "Financial"
const { MonthPicker, RangePicker, WeekPicker } = DatePicker;
const { Option } = Select;

class MixedChart extends React.Component {

 constructor(props) {
    super(props);
   this.state = {

mixed_chart_options:  {
            dataLabels: {
              enabled: false
            },

            stroke: {
              width:4
            },
            title: {
              text: '',
              align: 'left',
              offsetX: 110
            },
            xaxis: {
              categories: [],

              labels: {
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                }
            },
            yaxis: [
              {
                axisTicks: {
                  show: true,
                },
                axisBorder: {
                  show: true,
                  color: '#008FFB'
                },
                 labels: {
                   style: {
                    color: '#008FFB',
                  },
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                },
                title: {
                  text: "",
                  style: {
                    color: '#008FFB',
                  }
                },
                tooltip: {
                  enabled: true
                }
              },

              {
                seriesName: 'Income',
                opposite: true,
                axisTicks: {
                  show: true,
                },
                axisBorder: {
                  show: true,
                  color: '#00E396'
                },
                   labels: {
                   style: {
                    color: '#00E396',
                  },
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                },
                title: {
                  text: "",
                  style: {
                    color: '#00E396',
                  }
                },
              },
              {
                seriesName: 'Revenue',
                opposite: true,
                axisTicks: {
                  show: true,
                },
                axisBorder: {
                  show: true,
                  color: '#FEB019'
                },
                 labels: {
                   style: {
                    color: '#FEB019',

                  },
                    formatter: function(val) {
                       if (val < 100000) {
                                    return Math.round((val/1000) * 10)/10+'k';
                                }
                        else if (val >= 1000000){

                        return val=(val/1000000)+"M";

                        }
                         else {
                                    return val;
                                }

                    }
                },
                title: {
                  text: "",
                  style: {
                    color: '#FEB019',
                  }
                }
              },
            ],


            tooltip: {
              fixed: {
                enabled: true,
                position: 'topLeft', // topRight, topLeft, bottomRight, bottomLeft
                offsetY: 30,
                offsetX: 60
              },
            },
            legend: {
              horizontalAlign: 'left',
              offsetX: 40
            }
          },
mixed_chart_series: [{
name: 'Status',
type: 'column',
data: [10]
}, {
name: 'A',
type: 'column',
data: [20]
},

{
name: 'B',
type: 'column',
data: [30]
},
{
name: 'C',
type: 'column',
data: [150]
},


{
name: 'LINE',
type: 'line',
data: [40, 20]
},

],

      Headers:[],
     intHeader:[],
     filter:[],
     start_date:'',
     end_date:'',
     select_unit:[],
     select_unit_list:'',
     select_group:"Group",
     select_amount:"Amount",
     chart_data:[],
     donut_data:[],
     pie_data:[],
     bar_data:[],
     ve_bar_data:[],
     line_data:[{name:"", 'A':1, 'B':12}],
     field_list:["A","B"],
     chartData: [],
     sourceData :[],
     line_chart_source_data:[{name: 'A', value: 13000000}],
     sourceData_donut:[],
     value_mx : "Group",
     value_mz : "Group",
     value_my : "Amount",
     loading:true,
     mix_display:false,
     table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     table_data : [{ key: '1', oNumber: 'DVO524',}],


     load_table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     load_table_data : [{ key: '1', oNumber: 'DVO524',}],



     mix_table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     mix_table_data : [{ key: '1', oNumber: 'DVO524',}],

     download_mixedChart_table :[{ firstname: "Ravi", lastname: "Tomi", email: "ah@smthing.co.com" }],

   }
 }
// ============================= Loading all charts ====================================================================

componentWillMount() {
    this.loadHeaders();
     this.loadIntHeaders();
     this.loadMixedChartData({A:"Group", B:"Amount", C:"Group", start_date:"", end_date:"", unit_value:[], Menu:Menu_name})
  }

 async loadHeaders()
  {
    const promise = await axios.post(Host.loginURL +"/header_api", {Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);
      this.setState({Headers:data});
    }
  }

async loadIntHeaders()
  {
    const promise = await axios.post(Host.loginURL +"/int_Header_api", {Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);
      this.setState({intHeader:data});
      const d = [{'name': 'Actual','data': []}, {'name': 'Budget','data': []}]
      const dou = [1,3,4]
      this.setState({donut_data:dou})
      this.setState({chart_data:d})
       console.log(this.state.chart_data);

    }
  }

async loadMixedChartData(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }
    this.setState({mixed_chart_series : []})

    this.setState({mix_table_columns:[]})
     this.setState({mix_table_data:[]})


     this.setState({download_mixedChart_table:[]})
 const promise = await axios.post(Host.loginURL +"/mixed_dataView_api", options);
    const status = promise.status;
    if(status===200)
    {

      const sum_data = promise.data.data["sum_data"];
      const count_data = promise.data.data["count_data"];
      var table_dict = sum_data;
      this.setState(data);
      var i;
      var keys_list =[];
      var value_list =[];
      var max_chart_list = [];

      var table_column_list = [];
      var table_data_list = [];
      var table_data_dict = {}

      for (i = 0; i < Object.keys(sum_data).length; i++) {
            console.log(data[i])
            var key = Object.keys(sum_data)[i]
            keys_list.push(key)
            var value = sum_data[key]
            value_list.push(value)
            var data_dict = {};
            data_dict['name']=key;
