componentWillMount() {
    this.loadFilterHeaders();
     this.loadIntHeaders();
     this.loadMixedChartData({ValueX:this.state.selected_Xaxis,
                              ValueY:this.state.selected_Yaxis,
                              ValueZ:this.state.selected_Zaxis,
