        values.userName === this.state["first"] &&
        values.password === this.state["last"]
      ) {
        this.props.history.push("/workspace");
      }


      else {
        this.setState({
          msg: "Username & Password not correct"
        });
      }
    });

}


  render() {
    localStorage.clear();
    const { getFieldDecorator } = this.props.form;
    return (
     <div class="signup-form">
    <form action="/examples/actions/confirmation.php" method="post">
		<h2>Register</h2>
		<p class="hint-text">Create your account. It's free and only takes a minute.</p>
        <div class="form-group">
			<div class="row">
				<div class="col-xs-6"><input type="text" class="form-control"
				name="first_name" placeholder="First Name" required="required"/></div>
				<div class="col-xs-6"><input type="text" class="form-control"
				name="last_name" placeholder="Last Name" required="required"/></div>
			</div>
        </div>
        <div class="form-group">
        	<input type="email" class="form-control" name="email" placeholder="Email" required="required"/>
        </div>
		<div class="form-group">
            <input type="password" class="form-control" name="password" placeholder="Password" required="required"/>
        </div>
		<div class="form-group">
            <input type="password" class="form-control" name="confirm_password"
            placeholder="Confirm Password" required="required"/>
        </div>

		<div class="form-group">
            <button type="submit" class="btn btn-success btn-lg btn-block">Register Now</button>
        </div>
    </form>
	<div class="text-center">Already have an account? <a href="#">Sign in</a></div>
</div>
    );
  }
}


const WrappedLoginForm = Form.create()(LoginForm);

export default WrappedLoginForm;
