import React, { Component } from "react";

import { Row, Col, Card, Button, Icon, Progress } from "antd";
import ReactApexChart from 'react-apexcharts'
import ChartHistoryGroupColumn from "../components/charts/ChartHistoryGroupColumn";
const DataSet = require("@antv/data-set");
const HRNumbers = require('human-readable-numbers');
const NumericLabel = require('react-pretty-numbers');

const option = {
'justification': 'L',
'locales': 'en-US',
'currency':true,
'currencyIndicator': 'USD',
'percentage': false,
'precision': 2,
'wholenumber': null,
'commafy': true,
'cssClass': ['red'],
'shortFormat': true,
'shortFormatMinValue': 10000,
'shortFormatPrecision': 1,
'title': true
};



class Reactjs extends Component {

      constructor(props) {
        super(props);

        this.state = {
         options : {
            chart: {
                height: 350,
                type: 'bar',
                stacked: true,

            },
            plotOptions: {
                bar: {
                    horizontal: true,
                },

            },
            stroke: {
                width: 1,
                colors: ['#fff']
            },
            series2: [{
                name: 'Marine Sprite',
                data: [44, 55, 41, 37, 22, 43, 21]
            },{
                name: 'Reborn Kid',
                data: [25, 12, 19, 32, 25, 24, 10]
            }],
            title: {
                text: '100% Stacked Bar'
            },
            xaxis: {
                categories: [2008, 2009, 2010, 2011, 2012, 2013, 2014],

                labels: {
                    formatter: function(val) {
                        var string = <NumericLabel params={option}>12345.6789</NumericLabel>
                        return string

                    }
                }
            },

            tooltip: {
                    y: {
                        formatter: function(val) {
                         var yTicks = chart.yScale().ticks();
                          yTicks.interval(200000000);
                          var yLabels = chart.yAxis().labels();
                          yLabels.format("{%value}{scale: (1)(1000)(1000)(1000)|(k)(m)(M)(B)}");

                        return val + "K"
                    }
                }
            },
            fill: {
                opacity: 1

            },

            legend: {
                position: 'top',
                horizontalAlign: 'left',
                offsetX: 40
            }
        },
          series: [{
            data: [44, 55, 41, 64, 22, 43, 21]
          }, {
            data: [53, 32, 33, 52, 13, 44, 32]
          }],
        }
      }

      render() {
        return (


          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height="350" />
          </div>


        );
      }
    }

export default Reactjs;
