import React, { Component } from "react";
import {Link} from "react-router-dom";

import { Host } from  "../Host";
import axios from "axios";
import { Row, Col, Card, Form, Icon, Input, Button, Checkbox } from "antd";
const FormItem = Form.Item;


class SignupForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };

  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
    const u_name =  values.username;
    const p_word =  values.password;
    const email =  values.email;
    const phone =  values.phone;
    if(u_name == null){
     return false
    }
    else if(p_word == null){
     return false
    }
    else if(email == null){
     return false
    }
    else if(phone == null){
     return false
    }
    else{
        this.SignupAPI({username:values.username, password:values.password,
                     email:values.email, phone:values.phone})
    }
  });
};


  async SignupAPI(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }

    const promise = await axios.post(Host.loginURL+"/signup_api", options);
    const status = promise.status;
    if(status==200)
    {
      const data = promise.data.data;
      this.setState(data);
      this.props.history.push("/");
   }
   else {this.setState({msg: "Username & Password not correct"});}

}


  render() {
    localStorage.clear();
    const { getFieldDecorator } = this.props.form;
    return (
      <div className="login-form-container">
        <div className="header-login">
          <div className="login-logo">
            <img src="/assets/images/logo-white.png" alt="SQ"/>
          </div>
        </div>
        <div className="login-row">
          <div className="login-cell cell-login-hero" style={{backgroundImage: 'url(/assets/images/login-bg.jpg)'}}>
            <div className="login-cell-block">
              <div className="login-cell-content">
                <div className="login-hero-content">
                  <h1 className="login-heading">Welcome to SQ</h1>
                  <div className="login-subheading">
                    <p>
                    This is the new face of SQ. New look, new ideas, same core
                    </p>
                  </div>
                  <div className="website-link">
                    <Link to='http://webmamu.com/static/qcn/'>www.sqgc.com/</Link>
                  </div>
                </div>
              </div>Add
            </div>
          </div>
          <div className="login-cell cell-login-form">
            <div className="bg-image-mobile" style={{backgroundImage: 'url(/assets/images/login-bg.jpg)'}}></div>
            <div className="login-cell-block">
              <div className="login-logo text-center">
                <img src="/assets/images/logo-white.png" alt="SQ"/>
              </div>
              <div className="logo-with-form login-cell-content">
                <h4 className="mb-3">Signup with SQ</h4>
                <Form onSubmit={this.handleSubmit} className="login-form" >
                  <FormItem>
                    {getFieldDecorator("firstname", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Firstname!"
                        }
                      ]
                    })(
                      <Input size="large"
                      prefix={ <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />  }
                        placeholder="Firstname"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("lastname", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Lastname!"
                        }
                      ]
                    })(
                      <Input size="large"
                      prefix={ <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />  }
                        placeholder="Lastname"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("accountType", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your accountType!"
                        }
                      ]
                    })(
                      <Input size="large"
                      prefix={ <Icon type="profile" style={{ color: "rgba(0,0,0,.25)" }} />  }
                        placeholder="AccountType"
                      />
                    )}

                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("company", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your company!"
                        }
                      ]
                    })(
                      <Input size="large"
                      prefix={ <Icon type="office" style={{ color: "rgba(0,0,0,.25)" }} />  }
                        placeholder="Company"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("password", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Password!"
                        }
                      ]
                    })(
                      <Input
                        size="large"
                        prefix={
                          <Icon
                            type="lock"
                            style={{ color: "rgba(0,0,0,.25)" }}
                          />
                        }
                        type="password"
                        placeholder="Password"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("email", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Email!"
                        }
                      ]
                    })(
                      <Input
                        size="large"
                        prefix={
                          <Icon
                            type="mail"
                            style={{ color: "rgba(0,0,0,.25)" }}
                          />
                        }
                        type=""
                        placeholder="Email"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    {getFieldDecorator("phone", {
                      rules: [
                        {
                          required: true,
                          message: "Please input your Phone!"
                        }
                      ]
                    })(
                      <Input
                        size="large"
                        prefix={
                          <Icon
                            type="phone"
                            style={{ color: "rgba(0,0,0,.25)" }}
                          />
                        }
                        type=""
                        placeholder="Phone"
                      />
                    )}
                  </FormItem>
                  <FormItem>
                    <Button
                      type="primary"
                      htmlType="submit"
                      size="large"
                      className="login-form-button"
                      style={{ width: "100%" }}
                    >
                      Signup
                    </Button>
                  </FormItem>
                   <FormItem>
                    <Button
                      type="primary"
                      htmlType="submit"
                      size="large"
                      className="btn-info"
                      style={{ width: "100%" }}
                    >
                      <Link to='/' style={{textDecoration:"none"}}>Cancel</Link>
                    </Button>
                  </FormItem>
                  <FormItem>
                    <p style={{ color: "red" }}>{this.state.msg}</p>
                  </FormItem>
                </Form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}


const WrappedSignupForm = Form.create()(SignupForm);

export default WrappedSignupForm;
