class Child2 extends React.Component {
 constructor(props) {
    super(props)
    this.state = {
      sistemas: [],
      fromChild: 'empty',
      x:[{id: 1, name: 'Ravi', color: 'mokka raveendra'},
        {id: 1, name: 'Vamsi', color: 'mokka raveendra'},
         {id: 1, name: 'Raju', color: 'mokka raveendra'},
      {id: 1, name: 'Abu', color: 'mokka raveendra', 'user_status':'online'}],
      u:[{id: 1, name: 'Ravi', color: 'mokka raveendra', 'user_status':'offline'},
        {id: 1, name: 'Vamsi', color: 'mokka raveendra', 'user_status':'online'},
         {id: 1, name: 'Raju', color: 'mokka raveendra', 'user_status':'offline'},
      {id: 1, name: 'Abu', color: 'mokka raveendra', 'user_status':'online'}],
    };

  }

  update = (data) => {
    this.setState({fromChild: data})
    this.props.history.push("/test-react");


  }

  Change = (e) => {
    if(e.target.value != ''){


     var i;
              var ulist = this.state.x;
                for (i = 0; i < Object.keys(ulist).length; i++) {
                if(ulist[i]['name'] == e.target.value){

                    this.setState({x:[{id: 1, name:e.target.value}]})
                }


                }

    }
    else{
       this.setState({x:this.state.y})
    }


  }



  myFunction = (e) =>{
    var input, filter, ul, p, a, i, txtValue, li, li1;
    input = document.getElementById("myInput1");
    filter = input.value.toUpperCase();
    ul = document.getElementById("my_search");
    li = ul.getElementsByTagName("div");
    for (i = 0; i < li.length; i++) {
        a = li[i].getElementsByTagName("h5")[0];
        txtValue = a.textContent || a.innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
            li[i].style.display = "";
        } else {
            li[i].style.display = "none";
        }
    }
}

  render() {


    return (





                   <div className="inbox_chat scroll">
                      <div  id="my_search">
                          {this.state.u.map((value, index) =>

                            <div   className="chat_list active_chat" id="myUL"  key={index} onClick={()=>this.selectUser(value.username, value.id)}>
                              <div className="chat_people" value={this.state.select_username}>
                                    <img src="https://ptetutorials.com/images/user-profile.png" alt="sunil" className="chat_img" />

                                     <div>
                                         {value.user_status == 'online'?
                                             <div className="chat_ib">
                                              <h5 value={value.id} >{value.name} {value.id}
                                               <span className="online"></span>
                                               </h5>
                                              <p>new user for sq dashboard</p>
                                            </div>:

                                            <div className="chat_ib">
                                              <h5 value={value.id}>{value.name} {value.id}
                                               <span className="offline"></span>
                                               </h5>
                                              <p>new user for sq dashboard</p>
                                            </div>
                                         }
                                   </div>

                              </div>

                            </div>
                            )}
                        </div>

                      </div>



    );
  }
}
export default Child2;
