}


// ============================= Loading all charts ====================================================================

componentWillMount() {
    this.loadAnalytical();
     this.loadIntHeaders();
     this.loadAll_count_view();
  }

 async loadAnalytical()
  {
    const promise = await axios.post("http://127.0.0.1:8070/api/v1/header_api", { firstName: 'Mokka Ravi', lastName: 'Flintstone' });
