import React, { Component } from "react";

import { Row, Col, Card, Button, Icon, Progress } from "antd";

import ChartHistoryGroupColumn from "../components/charts/ChartHistoryGroupColumn";
import ChartBreakdownDonut from "../components/charts/ChartBreakdownDonut";
const DataSet = require("@antv/data-set");

const sourceDataWeekly = [
  {
    name: "Recieved",
    "Mooka.": 98.9,
    "Sun.": 28.8,
    "Mon.": 69.3,
    "Tue.": 81.4,
    "Wed.": 47,
    "Thu.": 20.3,
    "Fri.": 24
  },
  {
    name: "Sent",
    "Mooka.": 18.4,
    "Sun.": 33.2,
    "Mon.": 54.5,
    "Tue.": 99.7,
    "Wed.": 52.6,
    "Thu.": 45.5,
    "Fri.": 67.4
  }
];

class Reactjs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chartData: [],
      selectedKey: "daily"
    };
    this.onChangeChart = this.onChangeChart.bind(this);
  }



  componentDidMount() {
    const dv = new DataSet.View().source(sourceDataDaily);
    dv.transform({
      type: "fold",
      fields: ["1AM","2AM","3AM","4AM","5AM","6AM","7AM","8AM","9AM","10AM","11AM","12PM","1PM","2PM","3PM","4PM","5PM","6PM","7PM","8PM","9PM","10PM","11PM","12PM"],
      key: "keySelect",
      value: "average"
    });
    const data = dv.rows;
    this.setState({ chartData: data, selectedKey: "daily" });
  }

  onChangeChart(value) {
    if (value === "daily") {
      const dv = new DataSet.View().source(sourceDataDaily);
      dv.transform({
        type: "fold",
        fields: ["1AM","2AM","3AM","4AM","5AM","6AM","7AM","8AM","9AM","10AM","11AM","12PM","1PM","2PM","3PM","4PM","5PM","6PM","7PM","8PM","9PM","10PM","11PM","12PM"],
        key: "keySelect",
        value: "average"
      });
      const data = dv.rows;
      this.setState({ chartData: data, selectedKey: value });
    } else if (value === "weekly") {
      const dv = new DataSet.View().source(sourceDataWeekly);
      dv.transform({
        type: "fold",
        fields: ["Mooka.","Sun.","Mon.","Tue.","Wed.","Thu.","Fri."],
        key: "keySelect",
        value: "average"
      });
      const data = dv.rows;
      this.setState({ chartData: data, selectedKey: value });
    } else if (value === "monthly") {


      const dv = new DataSet.View().source(sourceDataMonthly);
      dv.transform({
        type: "fold",
        fields: ["Jan.","Feb.","Mar.","Apr.","May.","Jun.","Jul.","Sep.","Oct.","Nov.","Dec."],
        key: "keySelect",
        value: "average"
      });
      const data = dv.rows;
      this.setState({ chartData: data, selectedKey: value });
    }
    console.log("Selected :" + value);
    console.log(this.state.chartData);
  }

  render() {
     return (
      <Chart forceFit height={400} data={this.props.chartData}>
        <Tooltip />
        <Axis />
        <Legend />
        <Line
          position="keySelect*average"
          color="name"
          adjust={[{ type: "dodge", marginRatio: 1 / 32 }]}
        />
      </Chart>
    );
  }
}
export default Reactjs;
