import React, { Component } from "react";
import {Link, Switch, Route } from "react-router-dom";
import { Row, Col, Card, Button, Layout, Menu, Breadcrumb, Icon, Tree, Checkbox, Table} from 'antd';
import { Chart, Tooltip, Axis, Legend, Pie, Coord, Line, Bar, Point} from 'viser-react';
import { Host } from  "../../Host";
import axios from "axios";
import ReactApexChart from 'react-apexcharts'
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { CSVLink } from "react-csv";
import { css } from "@emotion/core";
import { RingLoader} from "react-spinners";
const DataSet = require('@antv/data-set');
const Menu_name = "Financial"
const { TreeNode, DirectoryTree } = Tree;

const { SubMenu } = Menu;
const { Header, Content, Footer, Sider } = Layout;

class GroupBarChart extends React.Component {

 constructor(props) {
    super(props);
   this.state = {

        group_bar_options: {
                    plotOptions: {
                      bar: {
                        horizontal: true,
                        dataLabels: {
                          position: 'top',
                        },
                      }
                    },
                    dataLabels: {
                      enabled: false,
                      offsetX: -6,
                      style: {
                        fontSize: '12px',
                        colors: ['#fff']
                      }
                    },
                    stroke: {
                      show: true,
                      width: 1,
                      colors: ['#fff']
                    },

                    xaxis: {
                      categories: [""],
                      labels: {
                            formatter: function(val) {
                               if (val < 100000) {
                                            return Math.round((val/1000) * 10)/10+'k';
                                        }
                                else if (val >= 1000000){

                                return val=(val/1000000)+"M";

                                }
                                 else {
                                            return val;
                                        }

                            }
                        }
                    },
                    yaxis: {
                      categories: [2001, 2002, 2003, 2004, 2005, 2006, 2007],
                      labels: {
                            formatter: function(val) {
                               if (val < 100000) {
                                            return Math.round((val/1000) * 10)/10+'k';
                                        }
                                else if (val >= 1000000){

                                return val=(val/1000000)+"M";

                                }
                                 else {
                                            return val;
                                        }

                            }
                        }
                    }
                  },

       checked: true,
        filterHeaders:[],
        intHeader:[],
         filter:[],
         selected_Xaxis: [{'checkedValue':'Group'}],
         selected_Yaxis: [{'checkedValue':'Amount'}],
         select_group:"Group",
         select_amount:"Amount",
     chart_data:[],
     donut_data:[],
     pie_data:[],
     bar_data:[],
     ve_bar_data:[],
     line_data:[{name:"", 'A':1, 'B':12}],
     field_list:["A","B"],
     chartData: [],
     sourceData :[],
     line_chart_source_data:[{name: 'A', value: 13000000}],
     sourceData_donut:[],
     value_xl : "Status",
     value_yl : "Amount_USD",
     loading:true,
     group_bar_display:false,

     table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     table_data : [{ key: '1', oNumber: 'DVO524',}],

     Gbar_table_columns : [{ title: 'Order Number',dataIndex: 'oNumber', key: 'oNumber',}],
     Gbar_table_data : [{ key: '1', oNumber: 'DVO524',}],
     download_group_barChart_table :[{ firstname: "Ravi", lastname: "Tomi", email: "ah@smthing.co.com" }],

   }
 }

// ============================= Loading all charts ====================================================================

onSelect = (keys, event) => {
    console.log('Trigger Select', keys, event);
  };

  onExpand = () => {
    console.log('Trigger Expand');
  };


  showModal = () => {
    alert('hi')
  };


onChange_Xaxis = (checkedValue) => {
  this.setState({checkVal:checkedValue});
  this.setState({selected_Xaxis:[]})
  const selectedOptions = {'checkedValue':checkedValue.target.value}
  this.state.selected_Xaxis.push(selectedOptions)
  if(checkedValue.target.checked == true){
        this.GroupBarData( {ValueX:this.state.selected_Xaxis,
                                  ValueY:this.state.selected_Yaxis, Menu:Menu_name})
  }
}


onChange_Yaxis = (checkedValue) => {
  this.setState({checkVal:checkedValue});
  const selectedOptions = {'checkedValue':'Amount'}
  this.state.selected_Yaxis.push(selectedOptions)

}




componentWillMount() {
    this.loadFilterHeaders();
     this.loadIntHeaders();
     this.loadAllChartData({ValueX:this.state.selected_Xaxis, ValueY:this.state.selected_Yaxis,  Menu:Menu_name});

  }

 async loadFilterHeaders()
  {
    const promise = await axios.post(Host.loginURL +"/header_api", {Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);
      this.setState({filterHeaders:data});
    }
  }

async loadIntHeaders()
  {
    const promise = await axios.post(Host.loginURL +"/int_Header_api",{Menu:Menu_name});
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      console.log(data);
      this.setState({intHeader:data});
      const d = [{'name': 'Actual','data': []}, {'name': 'Budget','data': []}]
      const dou = [1,3,4]
      this.setState({donut_data:dou})
      this.setState({chart_data:d})
       console.log(this.state.chart_data);

    }
  }

async loadAllChartData(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }
    this.setState({load_table_columns : []})
    this.setState({load_table_data : []})
    this.setState({line_data : []})
    this.setState({field_list : []})
    this.setState({line_chart_source_data : []})
    this.setState({chart_data:[]})
    this.setState({area_chart_series:[]})

     this.setState({download_lineChart_table:[]})
     this.setState({download_group_barChart_table:[]})
 const promise = await axios.post(Host.loginURL +"/dataView_api", options);
    const status = promise.status;

    if(status===200)
    {
      const data = promise.data.data;
      var l_data = [data];
      var table_dict = [data]
      this.setState(data);
      var i;
      var keys_list =[];
      var line_value_list =[];
      var line_chart_list = [];
      var load_bar_chart_list = [];
       var table_column_list = [];
       var table_data_list = [];
       var table_data_dict = {}
       var area_list = [];
       var area_chart_data = [];
      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]
            keys_list.push(key)
            var value = data[key]
            line_value_list.push(value)
            var data_dict = {};
            data_dict['name']=key;
            data_dict['value']=value;
            line_chart_list.push(data_dict)
            var load_data_dict = {};
            load_data_dict['name']=key;
            load_data_dict['data']=[value];
            load_bar_chart_list.push(load_data_dict)
            var column_dict = {}
            column_dict['title'] =key;
            column_dict['dataIndex'] =key;
            column_dict['key'] =key;
            table_data_dict[key] = value;
            table_column_list.push(column_dict)

            var area_dict = {}
            area_list.push(value);
            area_dict['name']=key;
            area_dict['data'] = area_list;
            area_chart_data.push(area_dict);


        }
       table_data_list.push(table_data_dict)


      this.setState({line_chart_source_data:line_chart_list})
      this.setState({field_list:keys_list})
      this.setState({chart_data:load_bar_chart_list})
      this.setState({area_chart_series:area_chart_data})


      this.setState({line_table_columns:table_column_list})
      this.setState({line_table_data:table_data_list})

       this.setState({Gbar_table_columns:table_column_list})
      this.setState({Gbar_table_data:table_data_list})

       this.setState({mix_table_columns:table_column_list})
      this.setState({mix_table_data:table_data_list})

       this.setState({area_table_columns:table_column_list})
      this.setState({area_table_data:table_data_list})


       this.setState({download_lineChart_table:table_dict})
       this.setState({download_group_barChart_table:table_dict})
       this.setState({download_areaChart_table:table_dict})
       this.setState({loading:false})
       this.setState({group_bar_display:true})
 }
}


//================================================ Group Bar Chart ====================================================================

async GroupBarData(data){

    const headers  = new Headers()
    headers.append('Content-Type', 'application/json');
    const options = {
    method : 'POST',
    headers,
    body:JSON.stringify(data),
    }
    this.setState({chart_data : []})
    this.setState({Gbar_table_columns:[]})
      this.setState({Gbar_table_data:[]})

      this.setState({download_group_barChart_table:[]})
 const promise = await axios.post(Host.loginURL +"/dataView_api", options);
    const status = promise.status;
    if(status===200)
    {
      const data = promise.data.data;
      var table_dict = data;
      this.setState(data);
      var i;
      var keys_list =[];
      var value_list =[];
      var chart_list = [];
       var table_column_list = [];
       var table_data_list = [];
       var table_data_dict = {}


      for (i = 0; i < Object.keys(data).length; i++) {
            console.log(data[i])
            var key = Object.keys(data)[i]
//            keys_list.push(key)
            var value = data[key]
//            value_list.push(value)
            var data_dict = {};
            data_dict['name']=key;
            data_dict['data']=[value];
            chart_list.push(data_dict)

            var column_dict = {}
            column_dict['title'] =key;
            column_dict['dataIndex'] =key;
            column_dict['key'] =key;
            table_data_dict[key] = value;
            table_column_list.push(column_dict)
        }

      table_data_list.push(table_data_dict)
      const g_data = [this.state.Actual, this.state.Budget]
      this.setState({g:g_data})
      this.setState({chart_data:chart_list})

      this.setState({Gbar_table_columns:table_column_list})
      this.setState({Gbar_table_data:table_data_list})

      this.setState({download_group_barChart_table:[table_dict]})
      this.setState({group_bar_display:true})
      this.setState({loading:false})

    }

}



render() {

const override = css`
  display: block;
  margin: 0 auto;
  border-color: red;
  loading: true;
  color: "red";
  css: "";
`;


  return (
   <div>

            <div style={{ background: '#fff', padding: 24, minHeight: 380 }}>
                <Content style={{ padding: '0 50px' }}>
                    <div className="page-header">
                          <Row type="flex" justify="space-between">
                            <Col>
                              <h1 className="page-heading"></h1>
                            </Col>
                            <Col>
                              <ul className="button-actions">
                                <li>
                                  <div>
                                    <Button type="primary" ghost>
                                      <Icon type="edit" />
                                      Edit
                                    </Button>
                                  </div>
                                </li>
                                <li>
                                  <div>
                                    <Button type="primary" ghost>
                                      <Icon type="delete" />
                                      Delete
                                    </Button>
                                  </div>
                                </li>
                                <li>
                                  <div>
                                    <Button type="primary" ghost  class="btn btn-primary" data-toggle="modal" data-target="#myModal_GroupBar">
                                     <Icon type="table" />  Table View
                                    </Button>
                                    <div class="modal fade" id="myModal_GroupBar" role="dialog">
                                        <div class="modal-dialog">
                                          <div class="modal-content">
                                            <div class="modal-header">
                                             {/* <button type="button" class="close" data-dismiss="modal" style={{marginLeft:"1px"}}>&times;</button> */}
                                              <h4 class="modal-title">Group Bar TableData</h4>
                                            </div>
                                            <div class="modal-body">
                                             <Table columns={this.state.Gbar_table_columns} dataSource={this.state.Gbar_table_data} style={{overflowY:"scroll"}}/>

                                            </div>
                                            <div class="modal-footer">
                                              <button type="button" class="btn btn-info" data-dismiss="modal">Close</button>
                                              <CSVLink data={this.state.download_group_barChart_table}>
                                              <button type="button" class="btn btn-primary" > <Icon type='download' /> Download </button>
                                              </CSVLink>;
                                            </div>
                                          </div>

                                        </div>
                                      </div>

                                  </div>
                                </li>
                              </ul>
                            </Col>
                          </Row>
                    </div>
                  <Layout style={{ padding: '24px 0', background: '#fff' }}>
                    <Sider width={200} >
                    <Menu mode="inline" defaultSelectedKeys={['1']} defaultOpenKeys={['sub1']} style={{ height: '100%' }} >
                        <SubMenu key="sub1" title={ <span> <Icon type="transaction" /> Financial </span> } >
                           <SubMenu key="subA"  defaultOpenKeys={['subA']}  title={ <span> <Icon type="filter" /> Xaxis </span> } >
                              {this.state.filterHeaders.map((value,index)=>
                                  <Menu.Item key="">
                                        <Checkbox onChange={this.onChange_Xaxis}
                                                value={value}  key={index}>{value}</Checkbox>
                                  </Menu.Item>
                              )}
                           </SubMenu>
                         {this.state.intHeader.map((value,index)=>
                           <SubMenu key="subB" title={ <span> <Icon type="filter" /> Yaxis </span> } >
                              <Menu.Item key="">
                                    <Checkbox onChange={this.onChange_Yaxis}
                                          checked={this.state.checked}  value={value}  key={index}>{value}</Checkbox>
                              </Menu.Item>
                           </SubMenu>
                         )}
                        </SubMenu>

                      <SubMenu key="sub2" title={ <span> <Icon type="solution" /> Operational </span> } >
                          <Menu.Item key="">
                                <Checkbox onChange={this.onChange}>Status</Checkbox>
                          </Menu.Item>

                          <Menu.Item key="">
                                <Checkbox onChange={this.onChange}>Type</Checkbox>
                          </Menu.Item>

                          <Menu.Item key="">
                                <Checkbox onChange={this.onChange}>Filter</Checkbox>
                          </Menu.Item>
                        </SubMenu>

                       <SubMenu key="sub3" title={ <span> <Icon type="home" /> Management </span> } >
                          <Menu.Item key="">
                                <Checkbox onChange={this.onChange} >Status</Checkbox>
                          </Menu.Item>

                          <Menu.Item key="">
                                <Checkbox onChange={this.onChange}>Type</Checkbox>
                          </Menu.Item>

                          <Menu.Item key="">
                                <Checkbox onChange={this.onChange}>Filter</Checkbox>
                          </Menu.Item>
                        </SubMenu>
                      </Menu>
                    </Sider>
                    <Content style={{ padding: '0 24px', minHeight: 280 }}>
                        <RingLoader
                      css={override}
                      size={150} // or 150px
                      color={"#123abc"}
                      loading={this.state.loading}
                      />
                  {this.state.group_bar_display == true?
                  <ReactApexChart options={this.state.group_bar_options} series={this.state.chart_data}
                                                                  type="bar" width="420" height="350" />:null
                   }
                    </Content>
                  </Layout>
              </Content>
           </div>
    </div>
    );
  }
}
export default GroupBarChart;
